import { useEffect,useState } from 'react';
import { Card,CardHeader,CardActionArea,CardMedia,CardContent, Typography,CardActions,makeStyles, Avatar,IconButton } from '@material-ui/core';
import { MoreVert,FavoriteBorder,ThumbUpAlt,Comment,Share } from '@material-ui/icons';
import {useCookies} from 'react-cookie';


const useStyles = makeStyles((theme) => ({
  media:{
    height: 250,
    [theme.breakpoints.down("sm")]:{
      height: 150,
    },
  },
  card:{
    marginBottom: theme.spacing(3),
  },
  tile:{
    height: theme.spacing(40),
    display: "flex",
    flexDirection: "column",
    margin: 5,

  },
  name:{
    fontSize: 16,
    fontWeight: 500,
    color: "#555",
  },
  card_action:{
    display: "flex",
    justifyContent: "space-between"
  },
  item:{
    display: "flex",
    alignItems: "center",
    spacing: theme.spacing(2),
    color: "#555",
  },
}));
export default function Post() {
  const classes = useStyles();
  const [token] = useCookies(['mytoken']);
  const [publication,setPublication] = useState([]);
  const [user,setUser] = useState([]);

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/publication/`,{
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
        'Authorization':`Token ${token['mytoken'].token}` 
      }
    })
    .then(resp => resp.json())
    .then(resp => setPublication(resp))
    .catch(error => console.log(error))

  }, []);

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/`,{
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setUser(resp))
    .catch(error => console.log(error))

  }, []);

  return (
    <div>
      {publication && publication.map(pub => {
        return(
          <div key = {pub.id}>
            {user && user.map(profile => {
              if (pub.user === profile.id){
                return(
                  <div key = {profile.id}>
                    <Card className={classes.card}>
                      <CardHeader 
                          avatar={
                            <Avatar src={profile.avatar}/>
                          }
                          action={
                            <IconButton aria-label="settings">
                            <MoreVert />
                            </IconButton>
                          }
                          title={
                            <Typography className={classes.name}> {profile.username}</Typography>
                          }
                          subheader="1 minute ago"
                      />
                      <CardActionArea>
                        <CardMedia className={classes.media} image={pub.image} />
                        <CardContent>
                          <Typography gutterBottom variant="h5">My first post</Typography>
                          <Typography variant="body">Lorem ipsum</Typography>
                        </CardContent>
                      </CardActionArea>
                      <CardActions className={classes.card_action}>
                        <div className={classes.item}>
                          <IconButton size="small">
                            <ThumbUpAlt/>
                          </IconButton>
                          <IconButton size="small">
                            <FavoriteBorder/>
                          </IconButton>
                          <Typography variant="body2">48K likes</Typography>
                        </div>
                        <div className={classes.item}>
                          <Typography variant="body2">2K Comments</Typography>

                          <IconButton size="small" className={classes.right}>
                            <Share/>
                          </IconButton>
                        </div>
                      </CardActions>
                    </Card>
                  </div>
                )
              }
            })
            }
          </div>
        )
      })}
    </div>
  );
}
