import React from 'react'
import { Remove,Add } from "@material-ui/icons";
import { connect } from "react-redux";
import './style.css'
export class index extends React.Component {
	constructor(props){
		super(props);
		this.id = this.props.match.params.id_prod;
		this.state={
			profile:[],
			quantity: 1,
		}
		this.cart = this.props.cart
	}
	handleQuantity=(type)=>{
  	if(type === "minus"){
  		this.state.quantity > 1 && this.setState({quantity: this.state.quantity-1})
  	}else{
  		this.state.quantity < 9 && this.setState({quantity: this.state.quantity+1})
  	}
  }
	render() {
		return (
			<div className="container">
					<table className="table-product">
						<thead>
							<tr>
								<td width="100">Product</td>
								<td width="250">Details</td>
								<td width="75">Price</td>
								<td width="150">Quantity</td>
								<td width="75">Action</td>
								<td width="150">Total</td>
							</tr>
						</thead>
						<tbody>
							{this.cart.products && this.cart.products.map((product)=>(

								<tr>
									<td height="120">
										<img src={product[0].pic1} alt="product"/>
									</td>
									<td>
										<p>{product[0].desc}</p>
									</td>
									<td>&euro;&nbsp;{product[0].price}</td>
									<td>
										<div className="input-numb">
											<div id="btn" onClick={()=>this.handleQuantity("minus")}><Remove htmlColor="#ff5959"/></div>
											<div className="numb"><input type="text" className="input-text" pattern="[0-9]*" value={product[0].quantity}/></div>
											<div id="btn" onClick={()=>this.handleQuantity("add")}> <Add htmlColor="#9dceff"/></div>
										</div>
									</td>
									<td>remove</td>
									<td>5</td>
								</tr>

							))}

						</tbody>
						<tfoot>
							<tr>
								<td colspan="5" align="right">Subtotal</td>
								<td>3</td>
							</tr>
						</tfoot>
					</table>
					<div className="amount">
						Order summary
					</div>
			</div>
		)
	}
}

const mapStateToProps = (state) => ({
  cart: state.cart,
});

export default connect(mapStateToProps)(index)
