import React from 'react'
import { withRouter } from "react-router";
import { withStyles,InputBase,Avatar,Typography } from '@material-ui/core';
import { Search,Image,EmojiEmotions,AttachFile,ThumbUp,Telegram,Videocam } from '@material-ui/icons';
import ScrollToBottom from 'react-scroll-to-bottom';
import Picker, { SKIN_TONE_MEDIUM_DARK } from "emoji-picker-react";

const useStyles = theme => ({
  container: {
    color: "#848484",
    position :"fixed",
    display: 'flex',
    margin: theme.spacing(2),
  },
  left:{
  	width: '20vw',
  	height: '100vh',
  	borderRight: '1px solid #3a3a3a',
  	paddingRight: theme.spacing(1),
  	paddingBottom: theme.spacing(1),
  },
  middle:{
  	width: '56vw',
  	height: '100vh',
  	paddingLeft: theme.spacing(1),
  	paddingRight: theme.spacing(1),
  	paddingBottom: theme.spacing(1),
  	position: 'relative',
  },
  right:{
  	width: '20vw',
  	borderLeft: '1px solid #3a3a3a',
  	paddingLeft: theme.spacing(1),
  	paddingBottom: theme.spacing(1),
  },
  search:{
  	width: '95%',
  	minHeight: '3.5%',
  	backgroundColor: '#3a3a3a',
  	padding: theme.spacing(0.5),
  	marginTop: theme.spacing(0.5),
  	alignItems: 'center',
  	display: 'flex',
  	borderRadius: theme.spacing(3),
  },
  list:{
  	marginTop: theme.spacing(1),
  	marginBottom: theme.spacing(1),
  	width: '95%',
  	maxHeight: '90%',
  	overflowY: 'auto',

  },
  item:{
  	display: 'flex',
  	padding: theme.spacing(1),
  	alignItems: 'center',
  	cursor: 'pointer',
  	"&:hover": {
        background: '#3a3a3a',
        borderRadius: theme.spacing(1),
    },
  },
  name:{
  	marginLeft: theme.spacing(1),
  	spacing: 0,
  },
  messenger:{
  	height: '84%',
  },
  bottom:{
  	backgroundColor: '#3a3a3a',
  	alignItems: 'center',
  	display: 'flex',
  	borderRadius: theme.spacing(3),
  },
  messageInput:{
  	width: '84%',
  	maxWidth:'84%',
  	height: '40px',
  	maxHeight:'40px',
    border: '1px solid #555',
    fontSize: '14px',
    fontWeight: 'bold',
    color: "#848484",
    paddingLeft: theme.spacing(1),
    paddingRight: theme.spacing(1),
    backgroundColor: '#3a3a3a',
    "&:focus": {
        outline: 'none',
    },
  },

 	message:{
		display: 'flex',
		padding: '10px',
		textTransform: 'capitalize',
	},
	messageText:{
	    padding: '10px',
	    borderRadius: '20px',
	    backgroundColor: '#666666',
	    color: 'black',
	    maxWidth: '80%',
	},
	messageOwn:{
		display: 'flex',
		flexDirection: 'column',
		alignItems: 'flex-end',
		padding: '10px',
		textTransform: 'capitalize',
	},

	messageOwnTex:{
		padding: '10px',
	    borderRadius: '20px',
	    backgroundColor: '#2a6db1',
	    color: 'white',
	    maxWidth: '80%',
	},
	emoji:{
		position: 'absolute',
		width: '36%',
		height: '41%',
		backgroundColor: '#3a3a3a',
		bottom: '80px',
		borderRadius: theme.spacing(2),
	},
})

export class Messenger extends React.Component {
	constructor(props){
	    super(props);
	    this.id = this.props.match.params.id;
	    this.refImg = React.createRef();
	    this.state={
	    	message:'',
	    	chosenEmoji: null,
	    	open: false,
	    };
	};
	sendMessage =()=>{
		this.setState({message:''})
	};

	onEmojiClick = (event, emojiObject) => {
	    this.setState({chosenEmoji: emojiObject})
	    this.setState({message: this.state.message+emojiObject.emoji})
	};

	imageHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          this.setState({image:reader.result})
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      const base64 = await this.convertBase64(file);
      this.setState({image:base64})
    };

  	convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);

        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
    };


	render() {
		const { classes } = this.props;
		return (
			<div className={classes.container}>
				<div className={classes.left}>
					All Friends
					<div className={classes.search}>
						<Search style={{marginRight: '4px'}}/>
						<InputBase
			                placeholder="Search... "
			                fullWidth
			                style={{color: '#848484', fontSize: '16px'}}
			            />
					</div>
					<div className={classes.list}>
						<div className={classes.item}>
							<Avatar />
							<div className={classes.name}>
								<Typography>Manohisoa</Typography>
								<Typography style={{fontSize: '10px'}}>Online</Typography>
							</div>
						</div>
					</div>
				</div>

				<div className={classes.middle}>
					<div style={{height: '5.5%',padding: '5px',backgroundColor: '#3a3a3a',justifyContent: 'space-between',alignItems:'center',display: 'flex'}}>
						<div style={{display: 'flex'}}>
							<Avatar />
							<div className={classes.name}>
								<Typography>Manohisoa</Typography>
								<Typography style={{fontSize: '10px'}}>Online</Typography>
							</div>
						</div>
						<Videocam />
					</div>
					<ScrollToBottom className={classes.messenger}>
						<div className={classes.message}>
							<div style={{display: 'flex'}}>
								<Avatar style={{marginRight: '10px'}}/>
								<div className={classes.messageText}>
									messages
								</div>
							</div>
						</div>

						<div className={classes.messageOwn}>
							<div className={classes.messageOwnTex}>
								messages
							</div>
						</div>
					</ScrollToBottom>
					<div className={classes.bottom}>
						<AttachFile style={{cursor: 'pointer',marginRight: '3px'}}/>
						<Image style={{cursor: 'pointer',marginRight: '3px'}} onClick={(event) => {
			                event.preventDefault();
			                this.refImg.current.click();
			                }}/>

						<input type='file' id='file-img' onChange={(e) => {
				                this.imageHandler(e);
				              }} ref={this.refImg} style={{display: 'none'}} accept=".png, .jpg, .jpeg"/>

						<EmojiEmotions style={{cursor: 'pointer',marginRight: '4px'}} onClick={() =>this.setState({open:!this.state.open})}/>
						<textarea
							className={classes.messageInput}
		                    placeholder="write something..."
		                    value = {this.state.message}
              				onChange = {e => this.setState({message:e.target.value})}
		                ></textarea>
		                {this.state.message ==='' ? (<ThumbUp style={{cursor: 'pointer',marginRight: '3px',marginLeft: '5px'}}/>) : (
		                <Telegram style={{cursor: 'pointer',marginRight: '3px',marginLeft: '5px'}} onClick={this.sendMessage}/>)}
					</div>
					{this.state.open && (
						<div className={classes.emoji}>
							<Picker
								style={{backgroundColor: '#3a3a3a'}}
						        onEmojiClick={this.onEmojiClick}
						        disableAutoFocus={true}
						        skinTone={SKIN_TONE_MEDIUM_DARK}
						        groupNames={{ smileys_people: "PEOPLE" }}
						        native
						    />
						</div>
					)}
				</div>

				<div className={classes.right}>
					Online Friends
					<div className={classes.search}>
						<Search style={{marginRight: '4px'}}/>
						<InputBase
			                placeholder="Search... "
			                fullWidth
			                style={{color: '#848484', fontSize: '16px' }}
			            />
					</div>
					<div className={classes.list}>
						<div className={classes.item}>
							<Avatar />
							<div className={classes.name}>
								<Typography>Manohisoa</Typography>
								<Typography style={{fontSize: '10px'}}>Online</Typography>
							</div>
						</div>
					</div>

				</div>
			</div>
		)
	}
}

export default withRouter(withStyles(useStyles)(Messenger))
