import React from 'react'
import { connect } from "react-redux";
import { publicRequest,userRequest } from '../../../requestMethods';
import './style.css'
export class index extends React.Component {
	constructor(props){
		super(props);
		this.id = this.props.match.params.id_prod;
		this.state={
			profile:[],
		}
	}
	handleQuantity=(type)=>{
  	if(type === "minus"){
  		this.state.quantity > 1 && this.setState({quantity: this.state.quantity-1})
  	}else{
  		this.state.quantity < 9 && this.setState({quantity: this.state.quantity+1})
  	}
  }

  getCart = async() =>{
		let data = await publicRequest.get('shop/cart')
	  .then(({data})=>data)
	  this.setState({cart: data})
	}
	componentDidMount(){
		this.getCart()
	 };
	render() {
		return (
			<div className="container cart-page">
				<table>
					<tr>
						<th width="75%">Products</th>
						<th>Quantity</th>
						<th>SubTotal</th>
					</tr>
					<tr>
						{this.props.cart.products.map((product)=>(
							<td>
								<div className="cart-info">
									<img src={product.pic1}  alt="" />
									<div>
										<p>Akanjo</p>
										<small>Price: $ 20</small>
									</div>
								</div>
							</td>
						))}
					</tr>
				</table>
			</div>
		)
	}
}

const mapStateToProps = (state) => ({
	user: state.user.currentUser,
  cart: state.cart,
});


export default connect(mapStateToProps)(index)
