import { useState,useEffect } from 'react';
import { AppBar,makeStyles, Toolbar, Typography,InputBase,alpha,Badge,Avatar,IconButton,
        Menu,MenuItem,Divider,Box,List,ListItem,ListItemText,ListItemAvatar } from '@material-ui/core';
import { Mail,Notifications,Search,Cancel,Person } from '@material-ui/icons';
import {useCookies} from 'react-cookie';

const useStyles = makeStyles((theme) => ({
  LogoLg:{
    display: "none",
    [theme.breakpoints.up("sm")]:{
      display: "block"
    }
  },
  LogoSm:{
    display: "block",
    [theme.breakpoints.up("sm")]:{
      display: "none"
    }
  },
  toolbar:{
    display: "flex",
    justifyContent: "space-between"
  },
  search:{
    display: "flex",
    alignItems: "center",
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    borderRadius: theme.shape.borderRadius,
    width: "40%",
    [theme.breakpoints.down("xs")]:{
      display: (props) => props.open ? "flex" : "none",
      width : "70%",
    }
  },
  input:{
    color: "white",
    marginLeft: theme.spacing(1)
  },
  cancel:{
    [theme.breakpoints.up("sm")]:{
      display: "none",
    },
  },
  searchbtn:{
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]:{
      display: "none",
    }
  },
  icons:{
    alignItems: "center",
    display: (props) => props.open ? "none" : "flex",

  },
  badge:{
    margin: theme.spacing(1),
  },
  paper:{

    maxHeight: 38 * 4.5,
    width: '20ch',
    overflow: 'visible',
    filter: 'drop-shadow(0px 2px 8px rgba(0,0,0,0.32))',
    transform: 'translateX(10px) translateY(50px)',
  },
}));
export default function NavBar() {
  const [open, setOpen] = useState(false);
  const classes = useStyles({ open });
  const [avatar, setAvatar] = useState([]);
  const [token] = useCookies(['mytoken']);

  const [anchorEl, setAnchorEl] = useState(null);
  const isMenuOpen = Boolean(anchorEl);

  const handleProfileMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${token['mytoken'].id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setAvatar(resp.avatar))
    .catch(error => console.log(error))

  }, [])

  const menuId = 'friend';
  const renderMenu = (
    <Menu
      anchorEl={anchorEl}
      getContentAnchorEl={null}
      id={menuId}
      keepMounted={false}
      anchorOrigin={{
        vertical: 'bottom',
        horizontal: 'right',
      }}
      transformOrigin={{
        vertical: 'top',
        horizontal: 'right',
      }}
      open={isMenuOpen}
      onClose={handleMenuClose}
      PaperProps={{
          elevation: 0,
          className:classes.paper,
        }}
    >
      <MenuItem onClick={handleMenuClose}>
        <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
          <ListItem alignItems="flex-start">
            <ListItemAvatar>
              <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
            </ListItemAvatar>
            <ListItemText
              primary="Brunch this weekend?"
              secondary={
                <Fragment>
                  <Typography
                    sx={{ display: 'inline' }}
                    component="span"
                    variant="body2"
                    color="text.primary"
                  >
                    Ali Connors
                  </Typography>
                  {" — I'll be in your neighborhood doing errands this…"}
                </Fragment>
              }
            />
          </ListItem>
          <Divider variant="inset" component="li" />
          <ListItem alignItems="flex-start">
            <ListItemAvatar>
              <Avatar alt="Travis Howard" src="/static/images/avatar/2.jpg" />
            </ListItemAvatar>
            <ListItemText
              primary="Summer BBQ"
              secondary={
                <Fragment>
                  <Typography
                    sx={{ display: 'inline' }}
                    component="span"
                    variant="body2"
                    color="text.primary"
                  >
                    to Scott, Alex, Jennifer
                  </Typography>
                  {" — Wish I could come, but I'm out of town this…"}
                </Fragment>
              }
            />
          </ListItem>
          <Divider variant="inset" component="li" />
          <ListItem alignItems="flex-start">
            <ListItemAvatar>
              <Avatar alt="Cindy Baker" src="/static/images/avatar/3.jpg" />
            </ListItemAvatar>
            <ListItemText
              primary="Oui Oui"
              secondary={
                <Fragment>
                  <Typography
                    sx={{ display: 'inline' }}
                    component="span"
                    variant="body2"
                    color="text.primary"
                  >
                    Sandra Adams
                  </Typography>
                  {' — Do you have Paris recommendations? Have you ever…'}
                </Fragment>
              }
            />
          </ListItem>
        </List>
      </MenuItem>
    </Menu>
  );

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="fixed">
          <Toolbar className={classes.toolbar}>
            <Typography variant="h6" className={classes.LogoLg}>
              Wanwork
            </Typography>
            <Typography variant="h6" className={classes.LogoSm}>
              Ww
            </Typography>
            <div className={classes.search}>
              <Search />
              <InputBase placeholder="Search..." className={classes.input}/>
              <Cancel className={classes.cancel} onClick={() => setOpen(false)} />
            </div>
            <div className={classes.icons}>
              <Search className={classes.searchbtn} onClick={() => setOpen(true)}/>
              <IconButton
                size="large"
                color="inherit"
                onClick={handleProfileMenuOpen}
              >
                <Badge badgeContent={4} color="secondary">
                  <Person />
                </Badge>
              </IconButton>
              <IconButton
                size="large"
                color="inherit"
              >
                <Badge badgeContent={4} color="secondary">
                  <Mail />
                </Badge>
              </IconButton>
              <IconButton
                size="large"
                color="inherit"
              >
                <Badge badgeContent={2} color="secondary">
                  <Notifications />
                </Badge>
              </IconButton>
              <Avatar src={avatar}/>
            </div>
          </Toolbar>
      </AppBar>
      {renderMenu}
    </Box>
  );
}
