import React, { useState } from 'react';
import { AppBar,makeStyles, Toolbar, Typography,InputBase,alpha,Badge,Avatar } from '@material-ui/core';
import { Mail,Notifications,Search } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
  LogoLg:{
    display: "none",
    [theme.breakpoints.up("sm")]:{
      display: "block"
    }
  },
  LogoSm:{
    display: "block",
    [theme.breakpoints.up("sm")]:{
      display: "none"
    }
  },
  toolbar:{
    display: "flex",
    justifyContent: "space-between"
  },
  search:{
    display: "flex",
    alignItems: "center",
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    borderRadius: theme.shape.borderRadius,
    width: "40%",
    [theme.breakpoints.down("sm")]:{
      display: (props) => props.open ? flex : "none",
    }
  },
  input:{
    color: "white",
    marginLeft: theme.spacing(1)
  },
  searchbtn:{
    marginRight: theme.spacing(2),
  },
  icons:{
    display: "flex",
    alignItems: "center"
  },
  badge:{
    marginRight: theme.spacing(2),
  },
}));
export default function NavBar() {
  const [open, setOpen] = useState(false);
  const classes = useStyles({ open });

  return (
    <AppBar>
        <Toolbar className={classes.toolbar}>
          <Typography variant="h6" className={classes.LogoLg}>
            Wanwork
          </Typography>
          <Typography variant="h6" className={classes.LogoSm}>
            Ww
          </Typography>
          <div className={classes.search}>
            <Search />
            <InputBase placeholder="Search..." className={classes.input}/>
          </div>
          <div className={classes.icons}>
            <Search className={classes.searchbtn} onClick={() => setOpen(true)}/>
            <Badge badgeContent={4} color="secondary" className={classes.badge}>
              <Mail />
            </Badge>
            <Badge badgeContent={2} color="secondary" className={classes.badge}>
              <Notifications />
            </Badge>
            <Avatar>W</Avatar>
          </div>
        </Toolbar>
    </AppBar>
  );
}
