import {useState, useRef,useEffect } from 'react';
import { Typography,Container,makeStyles,Card,CardMedia
        ,Avatar,CardContent,Badge,IconButton
        ,Grid,Button,ButtonGroup,TextField
        ,ImageList,ImageListItem,Tab,Tabs,Box,useTheme,AppBar } from '@material-ui/core';
import { CameraAlt,Edit,Room,Person,Cake,Group,GroupAdd,Info } from '@material-ui/icons';
import {useCookies} from 'react-cookie';
import { useParams } from 'react-router-dom';
import PropTypes from 'prop-types';
import SwipeableViews from 'react-swipeable-views';

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`full-width-tabpanel-${index}`}
      aria-labelledby={`full-width-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `full-width-tab-${index}`,
    'aria-controls': `full-width-tabpanel-${index}`,
  };
}


const useStyles = makeStyles((theme) => ({
  container:{
    display: "flex",
    border: "1px solid #ece7e7",
  },
  media:{
    height: 250,
    borderTopLeftRadius:theme.spacing(2),
    borderTopRightRadius:theme.spacing(2),
    [theme.breakpoints.down("sm")]:{
      height: 150,
    },
  },
  large: {
    width: theme.spacing(20),
    height: theme.spacing(20),
    top: theme.spacing(-10),
    border: `2px solid ${theme.palette.background.paper}`,
    [theme.breakpoints.down("sm")]:{
      width: theme.spacing(10),
      height: theme.spacing(10),
      top: theme.spacing(-5),
    },
  },
  iconBtn: {
    width: theme.spacing(2),
    height: theme.spacing(2),
    top: theme.spacing(-11),
    border: `2px solid ${theme.palette.background.paper}`,
    background: theme.palette.background.paper,
    "&:hover": {
          background: theme.palette.background.paper,
    },
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-6),
    },
  },
  iconBtn2: {
    top: theme.spacing(-38),
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-21),
    },
  },
  item:{
    display: "flex",
    alignItems: "center",
    marginTop: theme.spacing(0),
    [theme.breakpoints.up("sm")]:{
      marginTop: theme.spacing(1),
    },
  },
  icon:{
    marginRight: theme.spacing(1),
    [theme.breakpoints.up("sm")]:{
      fontSize: "18px",
    },
  },

  text:{
    fontWeight: 500,
  },
  appbar:{
    background: "#D3D3D3",
    color: "#696969",
    borderTopLeftRadius: 8,
    borderTopRightRadius: 8,
    boxShadow: "none",
    borderBottom: "1px solid #A9A9A9"
  },
  tab:{
    backgroundColor: "#DCDCDC",
    borderBottomLeftRadius: 8,
    borderBottomRightRadius: 8,
  },
}));

export default function CardProfile() {
  const classes = useStyles();
  const inputFile = useRef(null);
  const inputFile2 = useRef(null);
  const [avatar, setAvatar] = useState('/broken-image.jpg')
  const [img_covert, setImg_covert] = useState('https://material-ui.com/static/images/cards/paella.jpg');
  const [token] = useCookies(['mytoken']);
  //const [user, setUser] = useState([]);
  const { id } = useParams()

  const theme = useTheme();
  const [value, setValue] = useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleChangeIndex = (index) => {
    setValue(index);
  };

  const imageHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setAvatar(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      const base64 = await convertBase64(file);
      setAvatar(base64)
    };

  const imageCover = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setImg_covert(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const fileCov = e.target.files[0];
      const base64 = await convertBase64(fileCov);
      setImg_covert(base64)
    };

  const convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);

        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
    };
  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${token['mytoken'].id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => {
      setAvatar(resp.avatar)
      setImg_covert(resp.img_covert)})
    .catch(error => console.log(error))

  }, [])

  return (
      <>
        <CardMedia className={classes.media} image={img_covert} />
        <IconButton
          edge="end"
          className={classes.iconBtn2}
          onClick={(event) => {
            event.preventDefault();
            inputFile2.current.click();
          }}
        >
          <Edit />
        </IconButton>
        <Badge
            overlap="circular"
            anchorOrigin={{
              vertical: 'bottom',
              horizontal: 'right',
            }}
            badgeContent={
              <IconButton
                edge="end"
                className={classes.iconBtn}
                onClick={(event) => {
                event.preventDefault();
                inputFile.current.click();
                }}
               >
                <CameraAlt />
              </IconButton>
            }
          >
            <Avatar src={avatar} className={classes.large} />
        </Badge>

          <Grid container component="main">
            <Box sx={{ width: '100%', typography: 'body1' }}>
              <AppBar position="static" className={classes.appbar}>
                <Tabs
                  value={value}
                  onChange={handleChange}
                  indicatorColor="secondary"
                  textColor="inherit"
                  variant="scrollable"
                  scrollButtons="auto"

                  allowScrollButtonsMobile
                >
                  <Tab label="Profile" {...a11yProps(0)} style={{textTransform: 'none'}}/>
                  <Tab label="Friends" {...a11yProps(1)} style={{textTransform: 'none'}}/>
                  <Tab label="Following" {...a11yProps(2)} style={{textTransform: 'none'}}/>
                  <Tab label="Followers" {...a11yProps(3)} style={{textTransform: 'none'}}/>
                </Tabs>
              </AppBar>
              <SwipeableViews
                axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
                index={value}
                onChangeIndex={handleChangeIndex}
              >
                <TabPanel value={value} index={0} dir={theme.direction} className={classes.tab}>
                  <Grid container>
                    <Grid item xs={12} sm={6} spacing={2}>
                      <TextField
                        variant="outlined"
                        required
                        fullWidth
                        id="username"
                        label="Username"
                        name="username"
                        //value = {username}
                        //onChange = {e => setUsername(e.target.value)}
                      />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                      <TextField
                        variant="outlined"
                        required
                        fullWidth
                        id="username"
                        label="Username"
                        name="username"
                        //value = {username}
                        //onChange = {e => setUsername(e.target.value)}
                      />
                    </Grid>
                  </Grid>
                </TabPanel>
                <TabPanel value={value} index={1} dir={theme.direction} className={classes.tab}>
                  Item Two
                </TabPanel>
                <TabPanel value={value} index={2} dir={theme.direction} className={classes.tab}>
                  Item Three
                </TabPanel>
              </SwipeableViews>
            </Box>
          </Grid>
        <input type="file" accept="image/*" name="image-upload" id="input" ref={inputFile} style={{display: 'none'}}
            onChange={imageHandler}
          />
        <input type="file" accept="image/*" name="image-upload" id="input" ref={inputFile2} style={{display: 'none'}}
            onChange={imageCover}
          />
      </>
  );
}
