import React, { useState,useEffect, useRef } from 'react';
import { Notifications,
		 EnhancedEncryption,
		 Category,
		 Settings,Home,Menu,
		 ShoppingCart,ArrowDropDown,
		 ArrowRight,ArrowLeft,
		 FlashOn } from "@material-ui/icons";
import { CSSTransition } from 'react-transition-group';
//import Drawer from '../Drawer'
//import Feed from '../Feed'
import './style.css'

function Navbar(props) {
  return (
    <nav className="navbar">
      <ul className="navbar-nav">{props.children}</ul>
    </nav>
  );
}

function NavItem(props) {
  const [open, setOpen] = useState(false);

  return (
    <li className="nav-item">
      <a href="#" className="icon-button" onClick={() => setOpen(!open)}>
        {props.icon}
      </a>

      {open && props.children}
    </li>
  );
}

function DropdownMenu() {
  const [activeMenu, setActiveMenu] = useState('main');
  const [menuHeight, setMenuHeight] = useState(null);
  const dropdownRef = useRef(null);

  useEffect(() => {
    setMenuHeight(dropdownRef.current?.firstChild.offsetHeight)
  }, [])

  function calcHeight(el) {
    const height = el.offsetHeight;
    setMenuHeight(height);
  }

  function DropdownItem(props) {
    return (
      <a href="#" className="menu-item" onClick={() => props.goToMenu && setActiveMenu(props.goToMenu)}>
        <span className="icon-button">{props.leftIcon}</span>
        {props.children}
        <span className="icon-right">{props.rightIcon}</span>
      </a>
    );
  }

  return (
    <div className="dropdown" style={{ height: menuHeight }} ref={dropdownRef}>

      <CSSTransition
        in={activeMenu === 'main'}
        timeout={500}
        classNames="menu-primary"
        unmountOnExit
        onEnter={calcHeight}>
        <div className="menu">
          <DropdownItem>My Profile</DropdownItem>
          <DropdownItem
            leftIcon={<Settings />}
            rightIcon={<ArrowRight />}
            goToMenu="settings">
            Settings
          </DropdownItem>
          <DropdownItem
            leftIcon="ðŸ¦§"
            rightIcon={<ArrowRight />}
            goToMenu="animals">
            Animals
          </DropdownItem>

        </div>
      </CSSTransition>

      <CSSTransition
        in={activeMenu === 'settings'}
        timeout={500}
        classNames="menu-secondary"
        unmountOnExit
        onEnter={calcHeight}>
        <div className="menu">
          <DropdownItem goToMenu="main" leftIcon={<ArrowLeft />}>
            <h2>My Tutorial</h2>
          </DropdownItem>
          <DropdownItem leftIcon={<FlashOn />}>HTML</DropdownItem>
          <DropdownItem leftIcon={<FlashOn />}>CSS</DropdownItem>
          <DropdownItem leftIcon={<FlashOn />}>JavaScript</DropdownItem>
          <DropdownItem leftIcon={<FlashOn />}>Awesome!</DropdownItem>
        </div>
      </CSSTransition>

      <CSSTransition
        in={activeMenu === 'animals'}
        timeout={500}
        classNames="menu-secondary"
        unmountOnExit
        onEnter={calcHeight}>
        <div className="menu">
          <DropdownItem goToMenu="main" leftIcon={<ArrowLeft />}>
            <h2>Animals</h2>
          </DropdownItem>
          <DropdownItem leftIcon="ðŸ¦˜">Kangaroo</DropdownItem>
          <DropdownItem leftIcon="ðŸ¸">Frog</DropdownItem>
          <DropdownItem leftIcon="ðŸ¦‹">Horse?</DropdownItem>
          <DropdownItem leftIcon="ðŸ¦”">Hedgehog</DropdownItem>
        </div>
      </CSSTransition>
    </div>
  );
}

export class index extends React.Component {
	constructor(props){
		super(props);
		this.state={
			profile:[]
		};

	}

	getCurrentUser = async() =>{
    let data = await axios({
      method: 'get',
      url: apiService.url+`users/${this.user.id}/`,

    }).then(({data}) => data)
    this.setState({profile: data})
  };

  componentDidMount(){
    this.getCurrentUser()
  };
	render() {
		return (
			<Navbar>
				<NavItem icon={<ShoppingCart />}></NavItem>
				<NavItem icon={<Notifications/>}></NavItem>
				<NavItem icon={<ArrowDropDown />}>
					<DropdownMenu></DropdownMenu>
				</NavItem>
			</Navbar>
		)
	}
}

export default index
