import React from 'react'

import './style.css'

export class index extends React.Component {
	constructor(props){
		super(props);
		this.defaultDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
		this.state={
			values: ({
		          username: "",
		          password: "",
		        }),
			theme: '',
		};
	}

	preferedTheme = () =>{
	  	if (this.defaultDark){
	  		this.setState({theme: 'dark'})
	  	}
  	}

  	inputs = [
	    {
	      id: 1,
	      name: "username",
	      type: "text",
	      placeholder: "Username",
	    },
	    {
	      id: 2,
	      name: "password",
	      type: "password",
	      placeholder: "Password",
	    },
	  ];

	  onChange = (e) => {
	    this.setState({ values:{...this.state.values, [e.target.name]: e.target.value }});

	  };


	  creatAccount = (e) => {
	    this.props.history.push("/CreatUser");
	  };
	  login = () =>{

	    login(this.props.dispatchs, { username: this.state.values.username, password: this.state.values.password });
	  }

  	componentDidMount(){
	  	this.preferedTheme()
	  };
	render() {
		return (
			<div className="login" data-theme={this.state.theme}>
				<div className="left">
					<span>Login</span>
				</div>
				<div className="right">
					form
				</div>
			</div>
		)
	}
}

export default index
