import React from 'react'
import { Route,Switch,withRouter } from "react-router"
import {
  Badge
} from "@material-ui/core";
import { Notifications,
		 EnhancedEncryption,
		 Category,
		 Settings,Home,Menu,
		 ShoppingCart,ArrowDropDown,
		 ArrowRight,ArrowLeft,
		 FlashOn,Search } from "@material-ui/icons"
import Products from '../Products'

import axios from 'axios';
import apiService from '../../../APIService';
import { connect } from "react-redux";
import './style.css'
export class index extends React.Component {
	constructor(props){
		super(props);
		this.state={
			profile:[],
			search: "",
		};
		this.user = this.props.user;
    this.quantity = this.props.quantity;
	}

	getCurrentUser = async() =>{
    let data = await axios({
      method: 'get',
      url: apiService.url+`users/${this.user.id}/`,

    }).then(({data}) => data)
    this.setState({profile: data})
  };
  componentDidMount(){
    this.getCurrentUser()
  };
	render() {
		return (
			<div className="container">
				<div className="navigation">
					<ul>
						<li>
							<div className="ahref">
								<span className="icon"><Home /></span>
								<span className="title"> Shop</span>
							</div>
						</li>
						<li>
							<div className="ahref">
								<span className="icon"><Notifications /></span>
								<span className="title"> Notification</span>
							</div>
						</li>
						<li>
							<div className="ahref">
								<span className="icon"><EnhancedEncryption /></span>
								<span className="title"> Add products</span>
							</div>
						</li>
						<li>
							<div className="ahref">
								<span className="icon"><Category /></span>
								<span className="title"> Manage products</span>
							</div>
						</li>
						<li>
							<div className="ahref">
								<span className="icon"><Settings /></span>
								<span className="title"> Seller settings</span>
							</div>
						</li>
					</ul>
				</div>

				<div className="main">
					<div className="topbar">
						<div className="toggle">
							<Menu />
						</div>
						<div className="search">
							<label>
								<input type="text" placeholder="Search ..."/>
								<span><Search /></span>
							</label>
						</div>
						<div className="right">
							<Badge badgeContent={this.props.quantity} color="secondary">
		            <ShoppingCart className="custom-basket" />
		          </Badge>
							<div className="user">
								<img src={this.state.profile.avatar} alt=""/>
							</div>
						</div>
					</div>
					<Switch>
					<Route exact path={this.props.match.url}>
						<Products />
					</Route>
				</Switch>
				</div>
			</div>
		)
	}
}

const mapStateToProps = (state) => ({
  user: state.user.currentUser,
  quantity: state.cart.quantity,
});

export default connect(mapStateToProps)(withRouter(index))
