import {useState, useRef,useEffect } from 'react';
import { Typography,Container,makeStyles,Card,CardMedia
        ,Avatar,CardContent,Badge,IconButton
        ,Grid,Button,ButtonGroup
        ,ImageList,ImageListItem,Tab,Tabs,Box } from '@material-ui/core';
import { CameraAlt,Edit,Room,Person,Cake,Group,GroupAdd,Info } from '@material-ui/icons';
import {useCookies} from 'react-cookie';
import { useParams } from 'react-router-dom';
import { TabContext, TabList,TabPanel} from '@material-ui/lab';


const useStyles = makeStyles((theme) => ({
  container:{
    display: "flex",
    border: "1px solid #ece7e7",
  },
  media:{
    height: 250,
    [theme.breakpoints.down("sm")]:{
      height: 150,
    },
  },
  large: {
    width: theme.spacing(20),
    height: theme.spacing(20),
    top: theme.spacing(-10),
    border: `2px solid ${theme.palette.background.paper}`,
    [theme.breakpoints.down("sm")]:{
      width: theme.spacing(10),
      height: theme.spacing(10),
      top: theme.spacing(-5),
    },
  },
  iconBtn: {
    width: theme.spacing(2),
    height: theme.spacing(2),
    top: theme.spacing(-11),
    border: `2px solid ${theme.palette.background.paper}`,
    background: theme.palette.background.paper,
    "&:hover": {
          background: theme.palette.background.paper,
    },
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-6),
    },
  },
  iconBtn2: {
    top: theme.spacing(-38),
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-21),
    },
  },
  item:{
    display: "flex",
    alignItems: "center",
    marginTop: theme.spacing(0),
    [theme.breakpoints.up("sm")]:{
      marginTop: theme.spacing(1),
    },
  },
  icon:{
    marginRight: theme.spacing(1),
    [theme.breakpoints.up("sm")]:{
      fontSize: "18px",
    },
  },

  text:{
    fontWeight: 500,
  },
}));

export default function CardProfile() {
  const classes = useStyles();
  const inputFile = useRef(null);
  const inputFile2 = useRef(null);
  const [img1, setImg1] = useState('/broken-image.jpg')
  const [img2, setImg2] = useState('https://material-ui.com/static/images/cards/paella.jpg');
  const [token] = useCookies(['mytoken']);
  const [user, setUser] = useState([]);
  const { id } = useParams()

  const [value, setValue] = useState('1');

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };


  const imageHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setImg1(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      const base64 = await convertBase64(file);
      setImg1(base64)
    };

  const imageCover = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setImg2(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const fileCov = e.target.files[0];
      const base64 = await convertBase64(fileCov);
      setImg2(base64)
    };

  const convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);

        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
    };
  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${token['mytoken'].id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setUser(resp))
    .catch(error => console.log(error))

  }, [])

  return (
      <Card>
        <CardMedia className={classes.media} image={user.img_covert} />
        <IconButton
          edge="end"
          className={classes.iconBtn2}
          onClick={(event) => {
            event.preventDefault();
            inputFile2.current.click();
          }}
        >
          <Edit />
        </IconButton>
        <Badge
            overlap="circular"
            anchorOrigin={{
              vertical: 'bottom',
              horizontal: 'right',
            }}
            badgeContent={
              <IconButton
                edge="end"
                className={classes.iconBtn}
                onClick={(event) => {
                event.preventDefault();
                inputFile.current.click();
                }}
               >
                <CameraAlt />
              </IconButton>
            }
          >
            <Avatar src={user.avatar} className={classes.large} />
        </Badge>

        <CardContent>
          <Grid container component="main">
            <Box sx={{ width: '100%', typography: 'body1' }}>
              <TabContext value={value}>
                <Box style={{ borderBottom: 1, borderColor: 'divider' }}>
                  <TabList onChange={handleChange} aria-label="lab API tabs example">
                    <Tab label="Profile" value="1" />
                    <Tab label="friends" value="2" />
                    <Tab label="Following" value="3" />
                    <Tab label="Followers" value="4" />
                  </TabList>
                </Box>
                <TabPanel value="1">Item One</TabPanel>
                <TabPanel value="2">Item Two</TabPanel>
                <TabPanel value="3">Item Three</TabPanel>
              </TabContext>
            </Box>
          </Grid>
        </CardContent>
        <input type="file" accept="image/*" name="image-upload" id="input" ref={inputFile} style={{display: 'none'}}
            onChange={imageHandler}
          />
        <input type="file" accept="image/*" name="image-upload" id="input" ref={inputFile2} style={{display: 'none'}}
            onChange={imageCover}
          />
      </Card>
  );
}
