import React from 'react'
import { Card,CardHeader,CardActionArea,
        CardMedia,CardContent,
        CardActions,Collapse } from '@material-ui/core';
import { MoreVert,FavoriteBorder,Share,Telegram,PermMedia,Room,EmojiEmotions,Favorite,InsertComment } from '@material-ui/icons';
import { publicRequest,userRequest } from '../../requestMethods';
import { connect } from "react-redux";
import { withRouter } from "react-router";
import { Link } from 'react-router-dom';
import NumericLabel from 'react-pretty-numbers';
import _ from 'lodash';
import './style.css'
export class index extends React.Component {
	constructor(props){
	    super(props);
	    this.refImg = React.createRef();
	    this.state={
	            expanded:false,
	            anchorElP: '',
	            comment: '',
	            share: '',
	            comments: [],
	            publication: [],
	            users: [],
	            current: [],
	            image: '',
	            liked: false,
	            like: [],
	        };
	};

	handleExpandClick = (id) => {
	    this.setState({ [`expanded_${id}`]:  _.isUndefined(this.state[`expanded_${id}`])?true:!this.state[`expanded_${id}`] });
	};

	imageHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          this.setState({image:reader.result})
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      const base64 = await this.convertBase64(file);
      this.setState({image:base64})
    };

  	convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);

        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
    };

    getPub = async() =>{
	   await axios({
	        method: 'get',
	        url: this.state.next_url,
	        headers:{
	          Authorization: `Token ${this.user.token}`,
	        },
	      }).then(res =>{
	        var has_more = false
	        if(res.data.next){
	          has_more = true
	        }
	        this.setState({
	          next_url: res.data.next,
	          count: res.data.count,
	          publication: res.data.results,
	          more_exist: has_more,
	        })
	    })
	  };
	  getLike =   async() =>{
	    let data = publicRequest.get('like/')
	    .then(({data})=>data)
	    this.setState({like: data})
	  };

	  getCom = async() =>{
	  		let data = publicRequest.get('comment/')
	      	.then(({data}) => data)
	      	this.setState({comments: data})
	  };
	  getUser = async() =>{
	    let data = await axios({
	        method: 'get',
	        url: apiService.url+'users/',
	    }).then(({data}) => data)
	    this.setState({users: data})
	  };
	  getCurrentUser = async() =>{
	    let data = publicRequest.get(`users/${this.user.id}/`)
		.then(({data}) => data)
	    this.setState({current: data})
	  };

	  btnComment = async(content,id_post) => {
	    let author = parseInt(this.user.id, 10)
	    let post_connected = parseInt(id_post, 10)
	    const contents = {content:content,author:author,post_connected:post_connected}
	    let data = await axios.post(apiService.url+'comment/',contents)
	    .then(({data}) => data)
	    this.setState({comments: this.state.comments.concat(data)})
	    this.setState({comment:''})

	    };

	  btnShare = async()=>{
	    let author = parseInt(this.user.id, 10)
	    const contents = {user:author,message: this.state.share,image:this.state.image}
	    await axios.post(apiService.url+'publication/',contents,{
	      headers:{
	        Authorization: `Token ${this.user.token}`
	      }
	    }).then(res=>{
	      console.log(res)
	    })
	    this.setState({share:'',image:''})
	  };
	render() {
		return (
			<div className="postCont">
				<div className="stories">
					<div className="storie-card">
						<img src="https://cdn.pixabay.com/photo/2014/11/14/08/23/japan-530348_960_720.jpg" alt="" />
						<button>&#43;</button>
					</div>

					<div className="storie-card-right">
						<img src="https://cdn.pixabay.com/photo/2022/02/12/13/29/desert-7008952_960_720.jpg" alt="" />
						<img src="https://cdn.pixabay.com/photo/2021/11/19/04/23/lotus-6808054_960_720.jpg" alt="" />
					</div>
				</div>
				<div className="share-post">
					<div className="top">
						<img src="https://cdn.pixabay.com/photo/2021/11/19/04/23/lotus-6808054_960_720.jpg" alt="avatar" />
						<textarea type="text" placeholder="Type anything ..."></textarea>
					</div>
					<div className="bottom">
						<div className="options">
			              	<div className="option" onClick={(event) => {
				                event.preventDefault();
				                this.refImg.current.click();
				                }}>
				                <PermMedia htmlColor="tomato" className="shareIcon"/>
				                <span className="shareOptionText">Photo / video</span>
				            </div>
				            <input type='file' id='file-img' onChange={(e) => {
