import React from 'react'
import { withRouter } from "react-router";
import { FavoriteBorder,Favorite,AddShoppingCart,Remove,Add } from "@material-ui/icons";
import { publicRequest,userRequest } from '../../../requestMethods';
import { connect } from "react-redux";
import { addProduct } from "../../../context/cartRedux";
import "./style.css"

export class index extends React.Component {
	constructor(props){
		super(props);
		this.id = this.props.match.params.id_prod;
		this.state={
			quantity: 1,
			product: []
		}

	}

  handleQuantity=(type)=>{
  	if(type === "minus"){
  		this.state.quantity > 1 && this.setState({quantity: this.state.quantity-1})
  	}else{
  		this.state.quantity < 9 && this.setState({quantity: this.state.quantity+1})
  	}
  }

  	insertCart = async() =>{
		let user = this.props.user.id;
		let id_prod = this.id
		let quantity = this.state.quantity
		let data = await publicRequest.post('shop/cart/',{user,id_prod,quantity})
	  .then(({data})=>data)
	}

  addCart = (quantity) =>{
  		this.insertCart()
		this.props.dispatchs(
			addProduct({...this.state.product,quantity})
		);
	}

  getProduct = async() =>{
		let data = await publicRequest.get(`shop/product/${this.id}`)
	    .then(({data})=>data)
	    this.setState({product: data})
	}

	componentDidMount(){
		this.getProduct()
	};

	render() {
		return (
			<div className="details-cont">
				<div className="details">
					<div className="imgBox">
						<img id="img1" src={this.state.product.pic1} alt="product" />
						<div className="imgBottom">
							<img src={this.state.product.pic1} alt="products" />
							{this.state.product.pic2 && <img src={this.state.product.pic2} alt="products" />}
							{this.state.product.pic3 && <img src={this.state.product.pic3} alt="products" />}
							{this.state.product.pic4 && <img src={this.state.product.pic4} alt="products" />}
						</div>
					</div>
					<div className="info">
						<p>Categories/bags</p>
						<h2>{this.state.product.name}</h2>
						<h4>&euro;{this.state.product.price}</h4>
						<div className="input-numb">
							<div id="btn" onClick={()=>this.handleQuantity("minus")}><Remove htmlColor="#ff5959"/></div>
							<div className="numb"><input type="text" className="input-text" pattern="[0-9]*" value={this.state.quantity}/></div>
							<div id="btn" onClick={()=>this.handleQuantity("add")}> <Add htmlColor="#9dceff"/></div>
						</div>
						<h3>Poduct details</h3>
						<p>{this.state.product.desc}</p>
					</div>
				</div>
			</div>
		)
	}
}

const mapDispatchToProps = (dispatch) => ({
  dispatchs: dispatch,
});

const mapStateToProps = (state) => ({
  user: state.user.currentUser,
});
export default connect(mapStateToProps,mapDispatchToProps)(withRouter(index))
