import React from 'react'
import { Home,People,List,Image,PlayCircleOutline,Settings,LocalMall } from '@material-ui/icons';
import { Container,withStyles,Typography } from '@material-ui/core';

const useStyles = theme => ({
  container: {
    height: "55vh",
    minWidth: "15vw",
    color: "#848484",
    position :"fixed",
    paddingTop: theme.spacing(10),
    borderRight: "1px solid #3e3e3e",
    borderBottom: "1px solid #3e3e3e",
    overflowY: 'auto',
  },

  item:{
    display: "flex",
    alignItems: "center",
    width: '90%',
    padding: theme.spacing(1),
    "&:hover": {
          background: '#454545',
    },
    [theme.breakpoints.up("sm")]:{
      cursor: "pointer",
    },
  },
  icon:{
    marginRight: theme.spacing(1),
    [theme.breakpoints.up("sm")]:{
      fontSize: "24px",
    },
  },

  text:{
    [theme.breakpoints.down("xs")]:{
      display: "none",
    },
  },

})

export class LeftBar extends React.Component {
  render() {
    const { classes } = this.props;
    return (
      <div className={classes.container}> 
        <div className={classes.item}>
          <Home className={classes.icon}/>
          <span className={classes.text}>Home</span>
        </div>

        <div className={classes.item}>
          <People className={classes.icon}/>
          <span className={classes.text}>Friends</span>
        </div>

        <div className={classes.item}>
          <List className={classes.icon}/>
          <span className={classes.text}>Lists</span>
        </div>

        <div className={classes.item}>
          <Image className={classes.icon}/>
          <span className={classes.text}>Image Gallery</span>
        </div>

        <div className={classes.item}>
          <PlayCircleOutline className={classes.icon}/>
          <span className={classes.text}>Videos</span>
        </div>

        <div className={classes.item}>
          <LocalMall className={classes.icon}/>
          <span className={classes.text}>Shop</span>
        </div>
      </div>
    )
  }
}

export default withStyles(useStyles)(LeftBar)
