import React, { Component } from 'react'
import {
  Person, MenuBook, Room,
  Cake, Favorite, People, Email
} from '@material-ui/icons';
import { connect } from "react-redux";
import { Route, Switch, withRouter, Link } from 'react-router-dom';
import { publicRequest, userRequest } from '../../requestMethods';
import './style.css'
export class index extends Component {
  constructor(props) {
    super(props);
    this.reftextarea = React.createRef();
    this.state = {
      disabled: true,
      profile: ({
        covert: '',
        avatar: '',
        about: '',
      }),
    }
  };
  activeOrnot = () => {
    this.setState({ disabled: !this.state.disabled })
  }
  getProfile = async () => {
    let data = await userRequest.get(`userapp/users/${this.props.user.id}`).then(({ data }) => data)
    this.setState({ profile: { covert: data.img_covert, avatar: data.avatar, about: data.about_me } })
  }
  updateData = async (event) => {
    let data = await publicRequest.patch(`userapp/users/${this.props.user.id}/`, { [event.target.name] : event.target.value }).then(({ data }) => data)
    this.setState({ profile: { covert: data.img_covert, avatar: data.avatar, about: data.about_me } })
    this.setState({ disabled: !this.state.disabled })

  }
  componentDidMount() {
    this.getProfile()
  };
  render() {
    return (
      <div className='perso-container'>
        <div className='perso-header'>
          <div className='header-top'>
            <img src={this.state.profile.covert} alt='mur' />
            <img src={this.state.profile.avatar} alt='avatar' />

          </div>

          <div className='menu'>
            <ul>
              <li>Image</li>
              <li>Videos</li>
              <li>Pages</li>
              <li>Groups</li>
              <div className="animation start"></div>
            </ul>
          </div>
        </div>
        <div className='perso-footer'>
          <div className='footer-left'>
            <div className='top'>
              <span>Personal infos</span>
            </div>
            <span>About</span>
            <div className='input-data'>
              {this.state.disabled ?
                <small>{this.state.profile.about}</small> :
                <textarea disabled={(this.state.disabled) ? "disabled" : ""}
                  name='about_me'
                  //value={this.state.profile.about}
                  onChange={this.handleChange} />}
              {this.state.disabled ? <span onClick={this.activeOrnot}>&#9998;</span> : <button onClick={() => this.updateData('about_me', this.state.profile.about)}>&#10003;</button>}
            </div>
          </div>
          <div className='footer-right'>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  user: state.user.currentUser,
});
export default connect(mapStateToProps, null)(withRouter(index))
