import React, {useState, useRef } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Badge from '@material-ui/core/Badge';
import CameraAltIcon from '@material-ui/icons/CameraAlt';
import IconButton from '@material-ui/core/IconButton';
import DateFnsUtils from '@date-io/date-fns';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import {useHistory} from 'react-router-dom';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';
import APIService from '../APIService';
import EditIcon from '@material-ui/icons/Edit';
function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: 'url(https://source.unsplash.com/random)',
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    margin: theme.spacing(0,1),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  large: {
    width: theme.spacing(10),
    height: theme.spacing(10),
    border: `2px solid ${theme.palette.background.paper}`,
  },
  iconBtn: {
    width: theme.spacing(2),
    height: theme.spacing(2),
    border: `2px solid ${theme.palette.background.paper}`,
    background: theme.palette.background.paper,
    "&:hover": {
          background: theme.palette.background.paper,
    },
  },
  iconBtn2: {
    width: theme.spacing(2),
    height: theme.spacing(2),
    border: `2px solid ${theme.palette.background.paper}`,
    background: theme.palette.background.paper,
    "&:hover": {
          background: theme.palette.background.paper,
    },
  },
  profileCover:{
    height: 80,
    position: 'relative',
    top: 0,
    right:0,
    left:0,
  },
  profileCoverImg:{
    width: "100vh",
    height: 100,
    objectFit: "cover",
  },
}));

export default function SignInSide() {
  const classes = useStyles();
  let history = useHistory()

  const inputFile = useRef(null)
  const inputFile2 = useRef(null)
  const [first_name, setFirst_name] = useState('')
  const [last_name, setLast_name] = useState('')
  const [is_active, setIs_active] = useState('True')
  const [is_staff, setIs_staff] = useState('False')
  const [birth_date, setBirth_date] = useState('')
  const [address, setAddress] = useState('')
  const [about_me, setAbout_me] = useState('')
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [lastname, setLastname] = useState('')
  const [email, setEmail] = useState('')
  const [date, setDate] = useState(new Date())
    //const [token, setToken] = useCookies(['mytoken'])
  const [avatar, setAvatar] = useState('/broken-image.jpg')
  const [img_covert, setImg_covert] = useState('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRfRIXM7-2ET0SS_Z471QJwzBlkjqT4IepBCPXmlByfIIwViUiZAZtoMbAzyFHVNxQMKXs&usqp=CAU')

  const handleDateChange = (date) => {
      setDate(date);
    };

  const Login = () =>{ 
      history.push('/');
    }

  const registerBtn = () => {
        var d = new Date(); 
        var date_joined = d.getFullYear()+'/'+(d.getMonth()+1)+'/'+d.getDate()
        APIService.RegisterUser({username,email, password,first_name,last_name,is_active,is_staff,date_joined,birth_date,address,about_me,avatar,img_covert,})
        //.then(resp => setToken('mytoken',resp))
        .catch(error => console.log(error))

    }
  const imageHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setAvatar(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      const base64 = await convertBase64(file);
      setAvatar(base64)
    };

  const imageCover = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setImg_covert(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const fileCov = e.target.files[0];
      const base64 = await convertBase64(fileCov);
      setImg_covert(base64)
    };

  const convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);

        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
    };


  return (
    <Grid container component="main" className={classes.root}>
      <CssBaseline />
      <Grid item xs={false} sm={4} md={7} className={classes.image} />
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>

          
        <div className={classes.paper}>
          <div className={classes.profileCover}>
            <Badge
              overlap="circular"
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'right',
              }}
              badgeContent={
                <IconButton
                  edge="end"
                  onClick={(event) => {
                  event.preventDefault();
                  inputFile2.current.click();
                  }}
                 >
                  <EditIcon />
                </IconButton>
              }
            >
              <img
                className="profileCoverImg"
                src={img_covert}
                height="150"
                width= "100%"
              />
            </Badge>
          </div>
          <Badge
            overlap="circular"
            anchorOrigin={{
              vertical: 'bottom',
              horizontal: 'right',
            }}
            badgeContent={
              <IconButton
                edge="end"
                className={classes.iconBtn}
                onClick={(event) => {
                event.preventDefault();
                inputFile.current.click();
                }}
               >
                <CameraAltIcon />
              </IconButton>
            }
          >
            <Avatar src={avatar} className={classes.large} />
          </Badge>
          
          <input type="file" accept="image/*" name="image-upload" id="input" ref={inputFile} style={{display: 'none'}}
            onChange={imageHandler}
          />
          <input type="file" accept="image/*" name="image-upload" id="input" ref={inputFile2} style={{display: 'none'}}
            onChange={imageCover}
          />
          <br /><br />
          <form className={classes.form} noValidate>
            <Grid container spacing={1}>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  id="username"
                  label="Username"
                  name="username"
                  value = {username}
                  onChange = {e => setUsername(e.target.value)}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="email"
                  autoComplete="email"
                  value = {email}
                  onChange = {e => setEmail(e.target.value)}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  autoComplete="fname"
                  name="firstName"
                  variant="outlined"
                  fullWidth
                  id="firstName"
                  label="First Name"
                  autoFocus
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  variant="outlined"
                  fullWidth
                  id="lastName"
                  label="Last Name"
                  name="lastName"
                  autoComplete="lname"
                />
              </Grid>

              <Grid item xs={12} sm={6}>
                <TextField
                  name="adresse"
                  variant="outlined"
                  fullWidth
                  id="adresse"
                  label="Adresse"
                  autoFocus
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                  <KeyboardDatePicker
                    fullWidth
                    disableToolbar
                    variant="outlined"
                    format="dd/MM/yyyy"
                    margin="normal"
                    id="date-picker-inline"
                    label="Date of birth"
                    value={date}
                    onChange={handleDateChange}
                    KeyboardButtonProps={{
                      'aria-label': 'change date',
                    }}
                  />
                </MuiPickersUtilsProvider>
              </Grid>

              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  maxRows = "3"
                  multiline
                  fullWidth
                  id="aboutme"
                  label="About me"
                  name="aboutme"
                />
              </Grid>
              
              <Grid item xs={12} sm={6}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  id="password"
                  autoComplete="current-password"
                  value = {password}
                  onChange = {e => setPassword(e.target.value)}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  name="rpassword"
                  label="Reapet Password"
                  type="password"
                  id="password"
                  autoComplete="current-password"
                />
              </Grid>
              <Grid item xs={12}>
                <FormControlLabel
                  control={<Checkbox value="allowExtraEmails" color="primary" />}
                  label="I want to receive inspiration, marketing promotions and updates via email."
                />
              </Grid>
            </Grid>
            <Button
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
              onClick = {registerBtn}
            >
              Create
            </Button>
            <Grid container>
              <Grid item>
                <Link variant="body2" component="button" onClick = {Login}>
                  {"Already have an account?"}
                </Link>
              </Grid>
            </Grid>
            <Box mt={5}>
              <Copyright />
            </Box>
          </form>
        </div>
      </Grid>
    </Grid>
  );
}
