import React from 'react'
import { Route,Switch,withRouter } from "react-router"
import { connect } from "react-redux";
import { Menu,Notifications,ShoppingCart,
				Mail } from "@material-ui/icons"
import { publicRequest,userRequest } from '../../../requestMethods';
import Products from '../Products'
import './style.css'
export class index extends React.Component {
	constructor(props){
		super(props);
		this.state={
			profile:[],
		};
	}
	getCurrentUser = async() =>{
    let data = await publicRequest.get(`userapp/users/${this.props.user.id}/`)
    .then(({data}) => data)
    this.setState({profile: data})
  }
  componentDidMount(){
    this.getCurrentUser()
  };
	render() {
		return (
			<div className="shop">
				<div className="topbar">
					<div className="toggle" onClick={this.props.styleElement}>
						<Menu />
					</div>
					<div className="main-right">
						<div className="topbarStyle">
							<Notifications/>
						</div>
						<div className="topbarStyle">
							<Mail/>
						</div>
						<div className="topbarStyle">
							<ShoppingCart/>
						</div>
						<div className="topbarStyle">
							<img src={this.state.profile.avatar} alt="avatar" />
						</div>
					</div>
				</div>
				<Switch>
						<Route exact path='/'>
							<Products />
						</Route>

				</Switch>
			</div>
		)
	}
}

const mapStateToProps = (state) => ({
  user: state.user.currentUser,
  quantity: state.cart.quantity,
});

export default connect(mapStateToProps)(withRouter(index))
