import { Fab,makeStyles,
        Tooltip,
        Modal,
        Container, 
        TextField,
        RadioGroup,
        FormControlLabel,
        Radio,
        Button } from '@material-ui/core';
import { Add as AddIcon } from '@material-ui/icons';
import React, { useState } from 'react';

const useStyles = makeStyles((theme) => ({
  fab:{
    position: "fixed",
    bottom: 20,
    right: 20,
  },
  container:{
    width: 500,
    height: 550,
    backgroundColor: "white",
    position: "absolute",
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
    margin: "auto",
    [theme.breakpoints.down("xs")]:{
      width: "100vw",
      height: "100vh",
    },
  },
}));
export default function Add() {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [text, setText] = useState('')

  return (
    <>
      <Tooltip title="Add" arial-label="add" onClick={() => setOpen(true)}>
        <Fab color="primary" className={classes.fab}>
          <AddIcon />
        </Fab>
      </Tooltip>

      <Modal open={open}>
        <Container className={classes.container}>
          <form className={classes.form} autoComplete="off">
            <div className={classes.item}>
              <TextField
                      id="publication"
                      style={{ margin: 8 }}
                      placeholder="Title ..."
                      fullWidth
                      multiline
                      margin="normal"
                      InputLabelProps={{
                        shrink: true,
                      }}
                      value = {text}
                      onChange = {e => setText(e.target.value)}
                  />
            </div>
            <div className={classes.item}>
              <TextField
                      id="publication"
                      style={{ margin: 8 }}
                      placeholder="Description ..."
                      fullWidth
                      multiline
                      margin="normal"
                      InputLabelProps={{
                        shrink: true,
                      }}
                      value = {text}
                      onChange = {e => setText(e.target.value)}
                  />
            </div>

            <div className={classes.item}>
              <RadioGroup>
                <FormControlLabel value="friends" control={<Radio size="small"/>} label="Friends" />
                <FormControlLabel value="Public" control={<Radio size="small"/>} label="Public" />
              </RadioGroup>
            </div>
            <div className={classes.item}>
              <Button variant="outlined" color="primary" style={{marginRight:20}}>
              Create
              </Button>
              <Button variant="outlined" color="secondary">
              Cancel
              </Button>
            </div>
          </form>
        </Container>
      </Modal>
    </>
  );
}
