import {useState,useEffect } from 'react';
import { Typography,makeStyles,Card,CardMedia
        ,Avatar,CardContent
        ,Grid,Button,ButtonGroup
        ,ImageList,ImageListItem, Paper, Box } from '@material-ui/core';
import { Room,Person,Cake,Group,GroupAdd,Info } from '@material-ui/icons';
import {useCookies} from 'react-cookie';
import { useParams } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
  container:{
    display: "flex",
    border: "1px solid #ece7e7",
    top: theme.spacing(60),
    width: '53vw',
    position: 'absolute',
    padding: theme.spacing(1),
  },
  media:{
    height: 250,
    position: 'relative',
    top: theme.spacing(0),
    [theme.breakpoints.down("sm")]:{
      height: 150,
    },
  },
  large: {
    width: theme.spacing(20),
    height: theme.spacing(20),
    left: theme.spacing(40),
    top: theme.spacing(30),
    position: 'absolute',
    border: `2px solid ${theme.palette.background.paper}`,
    [theme.breakpoints.down("sm")]:{
      width: theme.spacing(10),
      height: theme.spacing(10),
      left: theme.spacing(21),
      top: theme.spacing(24),
    },
  },
  iconBtn: {
    width: theme.spacing(2),
    height: theme.spacing(2),
    top: theme.spacing(-11),
    border: `2px solid ${theme.palette.background.paper}`,
    background: theme.palette.background.paper,
    "&:hover": {
          background: theme.palette.background.paper,
    },
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-6),
    },
  },
  iconBtn2: {
    top: theme.spacing(-38),
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-21),
    },
  },
  item:{
    display: "flex",
    alignItems: "center",
    marginTop: theme.spacing(0),
    [theme.breakpoints.up("sm")]:{
      marginTop: theme.spacing(1),
    },
  },
  icon:{
    marginRight: theme.spacing(1),
    [theme.breakpoints.up("sm")]:{
      fontSize: "18px",
    },
  },

  text:{
    fontWeight: 500,
    color: '#555',
    textTransform: 'capitalize',
    position: 'absolute',
    left: theme.spacing(65),
    marginTop: theme.spacing(2),
    [theme.breakpoints.down("sm")]:{
      left: theme.spacing(22),
      top: theme.spacing(33),
    },
  },
  paper:{
    color: '#555',
    textTransform: 'capitalize',
    position: 'absolute',
    width: 100,
    left: theme.spacing(60),
    marginTop: theme.spacing(5),
    padding: theme.spacing(1),
    [theme.breakpoints.down("sm")]:{
      left: theme.spacing(22),
      top: theme.spacing(33),
    },
  },
  box_item:{
    display: 'flex',
    justifyContent: 'space-around',

  },
}));

export default function CardProfile() {
  const classes = useStyles();
  const [user, setUser] = useState([]);
  const { id } = useParams()




  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setUser(resp))
    .catch(error => console.log(error))

  }, [])

  return (
      <>
        <CardMedia className={classes.media} image={user.img_covert} />
        <Avatar src={user.avatar} className={classes.large} />
        <Typography className={classes.text}>
            @{user.username}
        </Typography>
        <Box
          sx={{
            display: 'flex',
            '& > :not(style)': {
              m: 1,
              width: '34vw',
              height: '5vh',

            },
          }}
        >
          <Paper elevation={0} className={classes.paper}>
            <div className={classes.box_item}>
              <Typography >
                  Friends
              </Typography>
              <Typography>
                  Followers
              </Typography>
              <Typography>
                  Following
              </Typography>
            </div>
            <div className={classes.box_item}>
              <Typography >
                  0 k
              </Typography>
              <Typography>
                  0 k
              </Typography>
              <Typography>
                  0 k
              </Typography>
            </div>
          </Paper>
        </Box>
        <div className={classes.container}>
          <Typography>
            Name
          </Typography>
        </div>
      </>
  );
}
