import { useEffect,useState,Fragment } from 'react';
import { Card,CardHeader,CardActionArea,
        CardMedia,CardContent, Typography,
        CardActions,makeStyles, 
        Avatar,IconButton,Collapse,Button,Grid,
        InputAdornment,FormControl,Input,List,
        ListItem,Divider,ListItemText,ListItemAvatar } from '@material-ui/core';
import { MoreVert,FavoriteBorder,ThumbUpAlt,Comment,Share,Telegram } from '@material-ui/icons';
import {useCookies} from 'react-cookie';
import {format} from 'timeago.js';
import { Link,useRouteMatch } from 'react-router-dom';
import NumericLabel from 'react-pretty-numbers';
import APIService from '../APIService';


const useStyles = makeStyles((theme) => ({
  media:{
    height: 250,
    [theme.breakpoints.down("sm")]:{
      height: 150,
    },
  },
  card:{
    marginBottom: theme.spacing(3),
  },
  tile:{
    height: theme.spacing(40),
    display: "flex",
    flexDirection: "column",
    margin: 5,

  },
  name:{
    fontSize: 16,
    fontWeight: 500,
    color: "#555",
  },
  card_action:{
    display: "flex",
    justifyContent: "space-between"
  },
  item:{
    display: "flex",
    alignItems: "center",
    spacing: theme.spacing(2),
    color: "#555",
  },
  link:{
    display: "flex",
    textDecoration: "none",
    color: "#555",
  },
}));
export default function Post() {
  const classes = useStyles();
  const [token] = useCookies(['mytoken']);
  const [publication,setPublication] = useState([]);
  const [user,setUser] = useState([]);
  const [current,setCurrent] = useState([]);
  const [comment,setComment] = useState('')
  const [comments,setComments] = useState([]);
  const [expanded, setExpanded] = useState(false);
  let { path, url } = useRouteMatch();

  const handleExpandClick = (id) => {
    setExpanded({
      ...expanded,
      [id]: !expanded[id]
    });
  };

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/publication/`,{
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
        'Authorization':`Token ${token['mytoken'].token}` 
      }
    })
    .then(resp => resp.json())
    .then(resp => setPublication(resp))
    .catch(error => console.log(error))

  }, []);

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/comment/`,{
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setComments(resp))
    .catch(error => console.log(error))

  }, []);

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/`,{
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setUser(resp))
    .catch(error => console.log(error))

  }, []);

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${token['mytoken'].id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setCurrent(resp))
    .catch(error => console.log(error))

  }, []);

  const btnComment = (content,id_post) => {
        var author = parseInt(token['mytoken'].id, 10)
        var post_connected = parseInt(id_post, 10)
        APIService.InsertComment({content,author, post_connected})
        //.then(resp => setToken('mytoken',resp))
        .catch(error => console.log(error))

    };
  return (
    <div>
      {publication && publication.map(pub => {
        return(
          <div key = {pub.id}>
            {user && user.map(profile => {
              if (pub.user === profile.id){
                return(
                  <div key = {profile.id}>
                    <Card className={classes.card}>
                      <CardHeader 
                          avatar={
                            <Avatar src={profile.avatar}/>
                          }
                          action={
                            <IconButton aria-label="settings">
                            <MoreVert />
                            </IconButton>
                          }
                          title={
                            <Link exact to={`${url}/profile/${profile.id}`} className={classes.link}>
                              <Typography className={classes.name}> {profile.username}</Typography>
                            </Link>
                          }
                          subheader={format(pub.date)}
                      />
                      <CardActionArea>
                        <CardMedia className={classes.media} image={pub.image} />
                        <CardContent>
                          <Typography gutterBottom variant="h5">{pub.title}</Typography>
                          <Typography variant="body">{pub.message}</Typography>
                        </CardContent>
                      </CardActionArea>
                      <CardActions className={classes.card_action}>
                        <div className={classes.item}>
                          <IconButton size="small">
                            <FavoriteBorder/>
                          </IconButton>
                          <Typography variant="body2"><NumericLabel params={{shortFormat: true,}}>1200</NumericLabel>&nbsp;likes</Typography>
                        </div>
                        <div className={classes.item}>
                          <Button className={classes.link} size="small" onClick={() => handleExpandClick(pub.id)}><NumericLabel params={{shortFormat: true,}}>{pub.comments_nbr}</NumericLabel>&nbsp; Comments</Button>

                          <IconButton size="small" className={classes.right}>
                            <Share/>
                          </IconButton>
                        </div>
                      </CardActions>
                      <Collapse in={expanded[pub.id]} timeout="auto" unmountOnExit>
                        <CardContent>
                           <div className={classes.item}>
                            <Avatar src={current.avatar}/>&emsp;
                            <FormControl fullWidth>
                                  <Input
                                    placeholder="Your comments "
                                    multiline
                                    value = {comment}
                                    onChange = {e => setComment(e.target.value)}
                                    endAdornment={
                                    <InputAdornment position="end">
                                      <IconButton
                                        aria-label="toggle password visibility"
                                        edge="end"
                                        onClick={() => btnComment(comment,pub.id)}
                                        >
                                        <Telegram />
                                      </IconButton>
                                      </InputAdornment>
                                    }
                                  />
                              </FormControl>

                           </div>
                           {user && user.map(author => {
                              return(
                                <div key={author.id}>
                                  {comments && comments.map(post_com => {
                                      if (author.id === post_com.author && post_com.post_connected === pub.id){
                                        return(
                                          <div key={post_com.id}>
                                            <div className={classes.item}>
                                              <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper', overflow: 'auto' }}>
                                                <ListItem alignItems="flex-start">
                                                  <ListItemAvatar>
                                                    <Avatar src={author.avatar} />
                                                  </ListItemAvatar>
                                                  <ListItemText
                                                    primary=<Link exact to={`${url}/profile/${author.id}`} className={classes.link}>{author.username}</Link>
                                                    secondary={
                                                      <Fragment>
                                                        <Typography
                                                          sx={{ display: 'inline' }}
                                                          component="span"
                                                          variant="body2"
                                                          color="text.primary"
                                                        >
                                                          {post_com.content}
                                                        </Typography>
                                                        {" — ("+format(post_com.date_posted)+")"}
                                                      </Fragment>
                                                    }
                                                  />
                                                </ListItem>
                                                <Divider variant="inset" component="li" />

                                              </List>
                                           </div>
                                          </div>
                                        )
                                      }
                                   })}
                                </div>
                              )

                           })}

                        </CardContent>
                      </Collapse>
                    </Card>
                  </div>
                )
              }
            })
            }
          </div>
        )
      })}
    </div>
  );
}
