import {useState,useEffect } from 'react';
import { Typography,makeStyles,Card,CardHeader,CardMedia,CardContent
        ,Avatar,Grid,Button,ButtonGroup,CardActionArea,CardActions
        ,ImageList,ImageListItem, Paper, Box,Table,IconButton,
        TableBody,TableCell,TableContainer,TableHead,TableRow } from '@material-ui/core';
import { Room,Person,Cake,Group,GroupAdd,Info,MoreVert,FavoriteBorder,Share } from '@material-ui/icons';
import {useCookies} from 'react-cookie';
import { useParams,Link,useRouteMatch } from 'react-router-dom';
import {format} from 'timeago.js';
import APIService from '../APIService';

const useStyles = makeStyles((theme) => ({
  container:{
    display: "flex",
    border: "1px solid #ece7e7",
    top: theme.spacing(55),
    width: '53vw',
    position: 'absolute',
    padding: theme.spacing(1),
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(40),
      width: '66vw',
    },
  },
  media:{
    height: 250,
    position: 'relative',
    top: theme.spacing(0),
    [theme.breakpoints.down("sm")]:{
      height: 150,
    },
  },
  large: {
    width: theme.spacing(20),
    height: theme.spacing(20),
    left: theme.spacing(40),
    top: theme.spacing(30),
    position: 'absolute',
    border: `2px solid ${theme.palette.background.paper}`,
    [theme.breakpoints.down("sm")]:{
      width: theme.spacing(10),
      height: theme.spacing(10),
      left: theme.spacing(21),
      top: theme.spacing(24),
    },
  },
  iconBtn: {
    width: theme.spacing(2),
    height: theme.spacing(2),
    top: theme.spacing(-11),
    border: `2px solid ${theme.palette.background.paper}`,
    background: theme.palette.background.paper,
    "&:hover": {
          background: theme.palette.background.paper,
    },
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-6),
    },
  },
  iconBtn2: {
    top: theme.spacing(-38),
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-21),
    },
  },
  item:{
    display: "flex",
    alignItems: "center",
    marginTop: theme.spacing(0),
    [theme.breakpoints.up("sm")]:{
      marginTop: theme.spacing(1),
    },
  },
  icon:{
    marginRight: theme.spacing(1),
    [theme.breakpoints.up("sm")]:{
      fontSize: "18px",
    },
  },

  text:{
    fontWeight: 500,
    color: '#555',
    textTransform: 'capitalize',
    position: 'absolute',
    left: theme.spacing(65),
    marginTop: theme.spacing(2),
    [theme.breakpoints.down("sm")]:{
      left: theme.spacing(22),
      top: theme.spacing(33),
    },
  },
  paper:{
    color: '#555',
    textTransform: 'capitalize',
    position: 'absolute',
    left: theme.spacing(60),
    marginTop: theme.spacing(5),
    padding: theme.spacing(1),
    [theme.breakpoints.down("sm")]:{
      left: theme.spacing(9),
      top: theme.spacing(33),
    },
  },
  box_item:{
    display: 'flex',
    justifyContent: 'space-around',
    [theme.breakpoints.down("xs")]:{
      display: 'flex',
      justifyContent: 'space-around',
    },
  },
  left:{
    border: "1px solid #f5f5f5",
    borderRadius: theme.spacing(1),
    padding: theme.spacing(1),
    color: '#555',
    backgroundColor: '#f5f5f5',
    maxHeight: 150,
  },
  right:{
    maxHeight: 300,
    overflowY: 'scroll',
  },
  card:{
    marginBottom: theme.spacing(3),
  },
  link:{
    display: "flex",
    textDecoration: "none",
    color: "#555",
    textTransform: 'capitalize',
  },
}));

export default function CardProfile() {
  const classes = useStyles();
  const [user, setUser] = useState([]);
  const { id } = useParams()
  const [token] = useCookies(['mytoken']);
  const [publication,setPublication] = useState([]);
  const [comment,setComment] = useState('');
