import React from 'react'
import { Remove,Add } from "@material-ui/icons";
import { connect } from "react-redux";
import { publicRequest,userRequest } from '../../../requestMethods';
import './style.css'
export class index extends React.Component {
	constructor(props){
		super(props);
		this.id = this.props.match.params.id_prod;
		this.state={
			profile:[],
		}
	}
	handleQuantity=(type)=>{
  	if(type === "minus"){
  		this.state.quantity > 1 && this.setState({quantity: this.state.quantity-1})
  	}else{
  		this.state.quantity < 9 && this.setState({quantity: this.state.quantity+1})
  	}
  }

  getCart = async() =>{
		let data = await publicRequest.get('shop/cart')
	  .then(({data})=>data)
	  this.setState({cart: data})
	}
	componentDidMount(){
		this.getCart()
	 };
	render() {
		return (
			<div className="Cart-Container">
				<div className="Header">
				 <h3 className="Heading">Shopping Cart</h3>
				 <h5 className="Action">Remove all</h5>
				</div>
				{this.props.cart.products.map((product)=>(
					<div className="Cart-Items">
						 <div className="image-box">
						 	<img src="" alt="" />
						 </div>
						 <div className="about">
							 <h1 className="title">Apple Juice</h1>
							 <h3 className="subtitle">250ml</h3>
						 </div>
						 <div className="counter">
							<div id="btn" onClick={()=>this.handleQuantity("minus")}><Remove htmlColor="#ff5959"/></div>
							<div className="numb"><input type="text" className="input-text" pattern="[0-9]*" value="1"/></div>
							<div id="btn" onClick={()=>this.handleQuantity("add")}> <Add htmlColor="#9dceff"/></div>
						 </div>
						 <div className="prices">
						 	 <div className="amount">$2.99</div>
							 <div className="save"><u>Save for later</u></div>
							 <div className="remove"><u>Remove</u></div>
						 </div>

					</div>
				))}
			</div>
		)
	}
}

const mapStateToProps = (state) => ({
	user: state.user.currentUser,
  cart: state.cart,
});


export default connect(mapStateToProps)(index)
