import React from 'react'
import { Remove,Add } from "@material-ui/icons";
import { AuthContext } from "../../../context/AuthContext";
import './style.css'
export class index extends React.Component {
	static contextType = AuthContext;
	constructor(props){
		super(props);
		this.id = this.props.match.params.id_prod;
		this.state={
			profile:[],
			number: 1,
		}

	}
	minus=()=>{
	  	if(this.state.number === 1){
	  		return null
	  	}else{
	  		this.setState({number: this.state.number-1})
	  	}
	  }

	  add=()=>{
	  	if(this.state.number === 9){
	  		return null
	  	}else{
	  		this.setState({number: this.state.number+1})
	  	}
	  }
	render() {
		return (
			<div className="container">
					<table className="table-product">
						<thead>
							<tr>
								<td width="150">Product</td>
								<td width="200">Details</td>
								<td width="75">Price</td>
								<td width="150">Quantity</td>
								<td width="150">Total</td>
							</tr>
						</thead>
						<tbody>
							<tr>
								<td height="175">1</td>
								<td>2</td>
								<td>3</td>
								<td>
									<div className="input-numb">
										<div id="btn" onClick={this.minus}><Remove htmlColor="#ff5959"/></div>
										<div className="numb"><input type="text" className="input-text" pattern="[0-9]*" value={this.state.number}/></div>
										<div id="btn" onClick={this.add}> <Add htmlColor="#9dceff"/></div>
									</div>
								</td>
								<td>5</td>
							</tr>
						</tbody>
						<tfoot>
							<tr>
								<td colspan="4" align="right">Total</td>
								<td>3</td>
							</tr>
						</tfoot>
					</table>
			</div>
		)
	}
}

export default index
