import React from 'react'
import { withRouter } from "react-router";
import { FavoriteBorder,Favorite,AddShoppingCart,Remove,Add } from "@material-ui/icons";
import { publicRequest,userRequest } from '../../../requestMethods';
import "./style.css"

export class index extends React.Component {
	constructor(props){
		super(props);
		this.id = this.props.match.params.id_prod;
		this.state={
			quantity: 1,
			product: []
		}

	}

  handleQuantity=(type)=>{
  	if(type === "minus"){
  		this.state.quantity > 1 && this.setState({quantity: this.state.quantity-1})
  	}else{
  		this.state.quantity < 9 && this.setState({quantity: this.state.quantity+1})
  	}
  }

  addCart = () =>{
  	console.log("Its okay")
  }

  getProduct = async() =>{
		let data = await publicRequest.get(`shop/product/${this.id}`)
	    .then(({data})=>data)
	    this.setState({product: data})
	}

	componentDidMount(){
		this.getProduct()
	};
	render() {
		return (
			<div className="details-cont">
				<div className="bottom">
					<div className="left">
						<div className="prod-pic">
							<img src={this.state.product.pic1} alt="products"/>
						</div>
						<div className="bottom-grid">
								<div className="item">
									<img src="https://cdn.pixabay.com/photo/2021/09/13/08/18/blue-flower-6620619_960_720.jpg" alt="products" />
								</div>

								<div className="item">
									<img src="https://cdn.pixabay.com/photo/2021/11/06/16/11/greece-6773683_960_720.jpg" alt="products" />
								</div>

								<div className="item">
									<img src="https://cdn.pixabay.com/photo/2017/03/31/15/35/leaves-2191649_960_720.jpg" alt="products" />
								</div>

								<div className="item">
									<img src="https://cdn.pixabay.com/photo/2021/10/16/06/13/girl-6714026_960_720.jpg" alt="products" />
								</div>
						</div>
					</div>
					<div className="right">
						<p>Categories/bags</p>
						<h2>{this.state.product.name}</h2>
						<h4>&euro;{this.state.product.price}</h4>
						<div className="input-numb">
							<div id="btn" onClick={()=>this.handleQuantity("minus")}><Remove htmlColor="#ff5959"/></div>
							<div className="numb"><input type="text" className="input-text" pattern="[0-9]*" value={this.state.quantity}/></div>
							<div id="btn" onClick={()=>this.handleQuantity("add")}> <Add htmlColor="#9dceff"/></div>
						</div>
						<h3>Poduct details</h3>
						<p>{this.state.product.desc}</p>
						<div className="btn-conf">
							<button id="btn1" onClick={this.addCart}><AddShoppingCart /></button>
							<button id="btn2"><FavoriteBorder /></button>
						</div>
					</div>
				</div>
			</div>
		)
	}
}

export default withRouter(index)
