import {useState, useRef,useEffect } from 'react';
import { Typography,Container,makeStyles,Card,CardMedia
        ,Avatar,CardContent,Badge,IconButton
        ,Grid,Button,ButtonGroup
        ,ImageList,ImageListItem } from '@material-ui/core';
import { CameraAlt,Edit,Room,Person,Cake,Group,GroupAdd,Info } from '@material-ui/icons';
import {useCookies} from 'react-cookie';
import { useParams } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
  container:{
    display: "flex",
    border: "1px solid #ece7e7",
  },
  media:{
    height: 250,
    [theme.breakpoints.down("sm")]:{
      height: 150,
    },
  },
  large: {
    width: theme.spacing(20),
    height: theme.spacing(20),
    top: theme.spacing(-10),
    border: `2px solid ${theme.palette.background.paper}`,
    [theme.breakpoints.down("sm")]:{
      width: theme.spacing(10),
      height: theme.spacing(10),
      top: theme.spacing(-5),
    },
  },
  iconBtn: {
    width: theme.spacing(2),
    height: theme.spacing(2),
    top: theme.spacing(-11),
    border: `2px solid ${theme.palette.background.paper}`,
    background: theme.palette.background.paper,
    "&:hover": {
          background: theme.palette.background.paper,
    },
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-6),
    },
  },
  iconBtn2: {
    top: theme.spacing(-38),
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-21),
    },
  },
  item:{
    display: "flex",
    alignItems: "center",
    marginTop: theme.spacing(0),
    [theme.breakpoints.up("sm")]:{
      marginTop: theme.spacing(1),
    },
  },
  icon:{
    marginRight: theme.spacing(1),
    [theme.breakpoints.up("sm")]:{
      fontSize: "18px",
    },
  },

  text:{
    fontWeight: 500,
  },
}));

export default function CardProfile() {
  const classes = useStyles();
  const inputFile = useRef(null);
  const inputFile2 = useRef(null);
  const [img1, setImg1] = useState('/broken-image.jpg')
  const [img2, setImg2] = useState('https://material-ui.com/static/images/cards/paella.jpg');
  const [token] = useCookies(['mytoken']);
  const [user, setUser] = useState([]);
  const { id } = useParams()


  const imageHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setImg1(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      const base64 = await convertBase64(file);
      setImg1(base64)
    };

  const imageCover = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setImg2(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const fileCov = e.target.files[0];
      const base64 = await convertBase64(fileCov);
      setImg2(base64)
    };

  const convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);

        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
    };
  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setUser(resp))
    .catch(error => console.log(error))

  }, [])

  return (
      <Card>
        <CardMedia className={classes.media} image={user.img_covert} />
        <Avatar src={user.avatar} className={classes.large} />

        <CardContent>
          <Grid container component="main">
            <Grid item sm={6} xs={12}>
              <div className={classes.item}>
                <Person className={classes.icon}/>
                <Typography className={classes.text}>
                  {user.username}
                </Typography>
              </div>
              <div className={classes.item}>
                <Cake className={classes.icon}/>
                <Typography className={classes.text}>
                  {user.birth_date}
                </Typography>
              </div>
              <div className={classes.item}>
                <Room className={classes.icon}/>
                <Typography className={classes.text}>
                  {user.address}
                </Typography>
              </div>
              <div className={classes.item}>
                <Info className={classes.icon}/>
                <Typography className={classes.text}>
                  {user.about_me}
                </Typography>
              </div>
              <div className={classes.item}>
                <Group className={classes.icon}/>
                <Typography className={classes.text}>
                  22 k
                </Typography>
              </div>
              <div className={classes.item}>
                <GroupAdd className={classes.icon}/>
                <Typography className={classes.text}>
                  22 k
                </Typography>
              </div>
              <div className={classes.item}>
                <ButtonGroup variant="text" color="primary" aria-label="text primary button group">
                  <Button>Follow</Button>
                  <Button>Add friend</Button>
                </ButtonGroup>
              </div>
            </Grid>
            <Grid item sm={6} xs={12}>
              <div className={classes.item}>
                <Typography className={classes.text}>
                  His gallery
                </Typography>
              </div>
              <div className={classes.item}>
                <ImageList rowHeight={100} style={{marginBottom:10}} cols={3}>
                  <ImageListItem >
                    <img src="https://material-ui.com/static/images/image-list/breakfast.jpg" alt="" />
                  </ImageListItem>

                  <ImageListItem >
                    <img src="https://material-ui.com/static/images/image-list/burgers.jpg" alt="" />
                  </ImageListItem>
                  <ImageListItem >
                    <img src="https://material-ui.com/static/images/image-list/camera.jpg" alt="" />
                  </ImageListItem>
                  <ImageListItem >
                    <img src="https://material-ui.com/static/images/image-list/morning.jpg" alt="" />
                  </ImageListItem>
                  <ImageListItem >
                    <img src="https://material-ui.com/static/images/image-list/hats.jpg" alt="" />
                  </ImageListItem>
                  <ImageListItem >
                    <img src="https://material-ui.com/static/images/image-list/honey.jpg" alt="" />
                  </ImageListItem>
                </ImageList>
              </div>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
  );
}
