import {useState, useRef } from 'react';
import { Typography,Container,makeStyles,Card,CardMedia,Avatar,CardContent,Badge,IconButton,Grid } from '@material-ui/core';
import { CameraAlt,Edit,Room,Person,Cake } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
  container:{
    display: "flex",
    border: "1px solid #ece7e7",
  },
  media:{
    height: 250,
    [theme.breakpoints.down("sm")]:{
      height: 150,
    },
  },
  large: {
    width: theme.spacing(20),
    height: theme.spacing(20),
    top: theme.spacing(-10),
    border: `2px solid ${theme.palette.background.paper}`,
    [theme.breakpoints.down("sm")]:{
      width: theme.spacing(10),
      height: theme.spacing(10),
      top: theme.spacing(-5),
    },
  },
  iconBtn: {
    width: theme.spacing(2),
    height: theme.spacing(2),
    top: theme.spacing(-11),
    border: `2px solid ${theme.palette.background.paper}`,
    background: theme.palette.background.paper,
    "&:hover": {
          background: theme.palette.background.paper,
    },
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-6),
    },
  },
  iconBtn2: {
    top: theme.spacing(-38),
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-21),
    },
  },
  item:{
    display: "flex",
    alignItems: "center",
    marginTop: theme.spacing(2),
    [theme.breakpoints.up("sm")]:{
      marginTop: theme.spacing(3),
    },
  },
  icon:{
    marginRight: theme.spacing(1),
    [theme.breakpoints.up("sm")]:{
      fontSize: "18px",
    },
  },

  text:{
    fontWeight: 500,
    [theme.breakpoints.down("xs")]:{
      display: "none",
    },
  },
}));

export default function CardProfile(props) {
  const classes = useStyles();
  const inputFile = useRef(null);
  const inputFile2 = useRef(null);
  const [img1, setImg1] = useState('/broken-image.jpg')
  const [img2, setImg2] = useState('https://material-ui.com/static/images/cards/paella.jpg');


  const imageHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setImg1(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      const base64 = await convertBase64(file);
      setImg1(base64)
    };

  const imageCover = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setImg2(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const fileCov = e.target.files[0];
      const base64 = await convertBase64(fileCov);
      setImg2(base64)
    };

  const convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);

        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
    };

  return (
      <Card>
        <CardMedia className={classes.media} image={img2} />
        <IconButton
          edge="end"
          className={classes.iconBtn2}
          onClick={(event) => {
            event.preventDefault();
            inputFile2.current.click();
          }}
        >
          <Edit />
        </IconButton>
        <Badge
            overlap="circular"
            anchorOrigin={{
              vertical: 'bottom',
              horizontal: 'right',
            }}
            badgeContent={
              <IconButton
                edge="end"
                className={classes.iconBtn}
                onClick={(event) => {
                event.preventDefault();
                inputFile.current.click();
                }}
               >
                <CameraAlt />
              </IconButton>
            }
          >
            <Avatar src={img1} className={classes.large} />
        </Badge>

        <CardContent>
          <Grid container component="main">
            <Grid item sm={6} xs={12}>
              <div className={classes.item}>
                <Person className={classes.icon}/>
                <Typography className={classes.text}>
                  Manohisoa
                </Typography>
              </div>
              <div className={classes.item}>
                <Cake className={classes.icon}/>
                <Typography className={classes.text}>
                  22-12-93
                </Typography>
              </div>
            </Grid>
          </Grid>
        </CardContent>
        <input type="file" accept="image/*" name="image-upload" id="input" ref={inputFile} style={{display: 'none'}}
            onChange={imageHandler}
          />
        <input type="file" accept="image/*" name="image-upload" id="input" ref={inputFile2} style={{display: 'none'}}
            onChange={imageCover}
          />
      </Card>
  );
}
