import React from 'react'
import { withRouter } from "react-router";
import { IconButton } from "@material-ui/core";
import { Info,FavoriteBorder,Favorite,AddShoppingCart,Euro } from "@material-ui/icons";
import { Link } from 'react-router-dom';
import { publicRequest,userRequest } from '../../../requestMethods';
import { addProduct } from "../../../context/cartRedux";
import { connect } from "react-redux";
import './style.css'

export class index extends React.Component {
	constructor(props){
		super(props);
		this.state={
			product: [],
			quantity: 1,
		}
		this.dispatch = this.props.dispatchs
	}

	getProduct = async() =>{
		let data = await publicRequest.get('shop/product')
	    .then(({data})=>data)
	    this.setState({product: data})
	}
	addCart = () =>{
		let product = this.state.product
		let quantity = this.quantity
		this.dispatch(
			addProduct({...product,quantity})
		);
	}
	componentDidMount(){
		this.getProduct()
	  };
	render() {
		return (
			<div className="product-cont">
				{this.state.product && this.state.product.map((prod, index) => {
					return(
						<div className="card" key={prod.id}>
							<div className="imgBox">
								<div className="price">
									&euro;
									<span>{prod.price}</span>
								</div>
								<img src={prod.pic1} alt="product" />
							</div>
							<div className="content">
								<h2>{prod.name}</h2>
								<p>{prod.desc}</p>
								<div className="btn">
									<button className="btn-by">By now</button>
									<div className="btn-content">
										<Link exact to={`${this.props.match.url}/products/${prod.id}`}><IconButton><Info htmlColor="skyblue"/></IconButton></Link>
										<IconButton><FavoriteBorder htmlColor="pink"/></IconButton>
										<IconButton onClick={this.addCart}><AddShoppingCart htmlColor="#63ea84"/></IconButton>
									</div>
								</div>
							</div>
						</div>
					)
				})}
			</div>
		)
	}
}
const mapDispatchToProps = (dispatch) => ({
  dispatchs: dispatch,
});
export default connect(null,mapDispatchToProps)(withRouter(index))
