import { useState,useEffect } from 'react';
import { AppBar,makeStyles, Toolbar, Typography,InputBase,alpha,Badge,Avatar,IconButton,
        Menu,MenuItem,Divider } from '@material-ui/core';
import { Mail,Notifications,Search,Cancel,Person } from '@material-ui/icons';
import {useCookies} from 'react-cookie';

const useStyles = makeStyles((theme) => ({
  LogoLg:{
    display: "none",
    [theme.breakpoints.up("sm")]:{
      display: "block"
    }
  },
  LogoSm:{
    display: "block",
    [theme.breakpoints.up("sm")]:{
      display: "none"
    }
  },
  toolbar:{
    display: "flex",
    justifyContent: "space-between"
  },
  search:{
    display: "flex",
    alignItems: "center",
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    borderRadius: theme.shape.borderRadius,
    width: "40%",
    [theme.breakpoints.down("xs")]:{
      display: (props) => props.open ? "flex" : "none",
      width : "70%",
    }
  },
  input:{
    color: "white",
    marginLeft: theme.spacing(1)
  },
  cancel:{
    [theme.breakpoints.up("sm")]:{
      display: "none",
    },
  },
  searchbtn:{
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]:{
      display: "none",
    }
  },
  icons:{
    alignItems: "center",
    display: (props) => props.open ? "none" : "flex",

  },
  badge:{
    margin: theme.spacing(1),
  },
}));
export default function NavBar() {
  const [open, setOpen] = useState(false);
  const classes = useStyles({ open });
  const [avatar, setAvatar] = useState([]);
  const [token] = useCookies(['mytoken']);

  const [anchorEl, setAnchorEl] = useState(null);
  const open1 = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${token['mytoken'].id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setAvatar(resp.avatar))
    .catch(error => console.log(error))

  }, [])

  return (
    <AppBar position="fixed">
        <Toolbar className={classes.toolbar}>
          <Typography variant="h6" className={classes.LogoLg}>
            Wanwork
          </Typography>
          <Typography variant="h6" className={classes.LogoSm}>
            Ww
          </Typography>
          <div className={classes.search}>
            <Search />
            <InputBase placeholder="Search..." className={classes.input}/>
            <Cancel className={classes.cancel} onClick={() => setOpen(false)} />
          </div>
          <div className={classes.icons}>
            <Search className={classes.searchbtn} onClick={() => setOpen(true)}/>
            <IconButton
              size="large"
              color="inherit"
            >
              <Badge badgeContent={4} color="secondary">
                <Person />
              </Badge>
            </IconButton>
            <IconButton
              size="large"
              color="inherit"
            >
              <Badge badgeContent={4} color="secondary">
                <Mail />
              </Badge>
            </IconButton>
            <IconButton
              size="large"
              color="inherit"
            >
              <Badge badgeContent={2} color="secondary">
                <Notifications />
              </Badge>
            </IconButton>
            <Avatar src={avatar}/>
          </div>
        </Toolbar>

        <Menu
          anchorEl={anchorEl}
          open={open}
          onClose={handleClose}
          onClick={handleClose}
          PaperProps={{
            elevation: 0,
            sx: {
              overflow: 'visible',
              filter: 'drop-shadow(0px 2px 8px rgba(0,0,0,0.32))',
              mt: 1.5,
              '& .MuiAvatar-root': {
                width: 32,
                height: 32,
                ml: -0.5,
                mr: 1,
              },
              '&:before': {
                content: '""',
                display: 'block',
                position: 'absolute',
                top: 0,
                right: 14,
                width: 10,
                height: 10,
                bgcolor: 'background.paper',
                transform: 'translateY(-50%) rotate(45deg)',
                zIndex: 0,
              },
            },
          }}
          transformOrigin={{ horizontal: 'right', vertical: 'top' }}
          anchorOrigin={{ horizontal: 'right', vertical: 'bottom' }}
        >
          <MenuItem>
            <Avatar /> Profile
          </MenuItem>
          <MenuItem>
            <Avatar /> My account
          </MenuItem>
          <Divider />
          <MenuItem>
            <ListItemIcon>
              <PersonAdd fontSize="small" />
            </ListItemIcon>
            Add another account
          </MenuItem>
          <MenuItem>
            <ListItemIcon>
              <Settings fontSize="small" />
            </ListItemIcon>
            Settings
          </MenuItem>
          <MenuItem>
            <ListItemIcon>
              <Logout fontSize="small" />
            </ListItemIcon>
            Logout
          </MenuItem>
        </Menu>

    </AppBar>
  );
}
