import React from 'react';
import { withStyles,Avatar,Typography,InputBase } from '@material-ui/core';
import { MoreVert,Telegram,PermMedia,EmojiEmotions,Close,Remove,PhotoCamera, } from '@material-ui/icons';
const useStyles = theme => ({
	container:{
		width: '400px',
		height: '450px',
		position: 'fixed',
		bottom:0,
		right: '30px',
		zIndex:2,
	},
	contItemBottom:{
		width: '100%',
		height: '100%',
		backgroundColor: 'lightgrey',
		padding: theme.spacing(1),
	},
	itemTop:{
		width: '100%',
		height: '10%',
		backgroundColor: 'dimgrey',
		borderTopLeftRadius: theme.spacing(2),
		borderTopRightRadius: theme.spacing(2),
		alignItems: 'center',
		justifyContent: 'space-between',
		padding: theme.spacing(1),
		display: 'flex',
		textTransform: 'capitalize',
		color: "#ececec",
		fontSize: "20px",
		fontWeight: 500,
	},
	messageBox:{
		width: '100%',
		height: '76%',
		textTransform: 'capitalize',
		fontSize: "20px",
		fontWeight: 500,
		position: 'realative',
	},
	inputCustom:{
		width: '95%',
		height: '8%',
		textTransform: 'capitalize',
		backgroundColor: ' #909497',
		borderRadius: theme.spacing(3),
		display: 'flex',
		alignItems: 'center',
		color: "#ececec",
		paddingLeft: '10px',
		paddingRight: '10px',
	},
	input:{
	    color: "white",
	    marginLeft: theme.spacing(1),
	    width: "80%",
	},
});
export class BoxMessage extends React.Component {
	constructor(props){
    	super(props);
	};
	render() {
		const { classes } = this.props;
		return (
			<div className={classes.container}>
				<div className={classes.itemTop}>
					<div style={{display: 'flex',alignItems: 'center'}}>
						<Avatar />
						<Typography style={{marginLeft: '6px',}} >
							orthencia
						</Typography>
					</div>
					<div style={{display: 'flex',alignItems: 'center'}}>
						<Remove style={{marginRight:'6px', cursor: 'pointer'}}/>
						<Close style={{cursor: 'pointer'}}/>
					</div>
				</div>
				<div className={classes.contItemBottom}>
					<div className={classes.messageBox}>
					</div>
					<div className={classes.inputCustom}>
						<PhotoCamera style={{cursor:'pointer'}}/>
						<EmojiEmotions style={{cursor:'pointer'}}/>
						<InputBase placeholder="Search..." className={classes.input}/>
						<Telegram style={{cursor:'pointer'}}/>
					</div>
				</div>
			</div>
		)
	}
}

export default withStyles(useStyles)(BoxMessage)
