import { useEffect,useState,Fragment } from 'react';
import { Card,CardHeader,CardActionArea,
        CardMedia,CardContent, Typography,
        CardActions,makeStyles, 
        Avatar,IconButton,Collapse,Button,Grid,
        InputAdornment,FormControl,Input,List,
        ListItem,Divider,ListItemText,ListItemAvatar } from '@material-ui/core';
import { MoreVert,FavoriteBorder,ThumbUpAlt,Comment,Share,Telegram } from '@material-ui/icons';
import {useCookies} from 'react-cookie';
import {format} from 'timeago.js';
import { Link,useRouteMatch } from 'react-router-dom';


const useStyles = makeStyles((theme) => ({
  media:{
    height: 250,
    [theme.breakpoints.down("sm")]:{
      height: 150,
    },
  },
  card:{
    marginBottom: theme.spacing(3),
  },
  tile:{
    height: theme.spacing(40),
    display: "flex",
    flexDirection: "column",
    margin: 5,

  },
  name:{
    fontSize: 16,
    fontWeight: 500,
    color: "#555",
  },
  card_action:{
    display: "flex",
    justifyContent: "space-between"
  },
  item:{
    display: "flex",
    alignItems: "center",
    spacing: theme.spacing(2),
    color: "#555",
  },
  link:{
    display: "flex",
    textDecoration: "none",
    color: "#555",
  },
}));
export default function Post() {
  const classes = useStyles();
  const [token] = useCookies(['mytoken']);
  const [publication,setPublication] = useState([]);
  const [user,setUser] = useState([]);
  const [current,setCurrent] = useState([])
  const [expanded, setExpanded] = useState(false);
  let { path, url } = useRouteMatch();

  const handleExpandClick = (id) => {
    setExpanded({
      ...expanded,
      [id]: !expanded[id]
    });
  };

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/publication/`,{
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
        'Authorization':`Token ${token['mytoken'].token}` 
      }
    })
    .then(resp => resp.json())
    .then(resp => setPublication(resp))
    .catch(error => console.log(error))

  }, []);

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/`,{
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setUser(resp))
    .catch(error => console.log(error))

  }, []);

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${token['mytoken'].id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setCurrent(resp))
    .catch(error => console.log(error))

  }, [])

  const linked = (id) => {
    console.log(id)
  };

  return (
    <div>
      {publication && publication.map(pub => {
        return(
          <div key = {pub.id}>
            {user && user.map(profile => {
              if (pub.user === profile.id){
                return(
                  <div key = {profile.id}>
                    <Card className={classes.card}>
                      <CardHeader 
                          avatar={
                            <Avatar src={profile.avatar}/>
                          }
                          action={
                            <IconButton aria-label="settings">
                            <MoreVert />
                            </IconButton>
                          }
                          title={
                            <Link exact to={`${url}/profile/${profile.id}`} className={classes.link}>
                              <Typography className={classes.name}> {profile.username}</Typography>
                            </Link>
                          }
                          subheader={format(pub.date)}
                      />
                      <CardActionArea>
                        <CardMedia className={classes.media} image={pub.image} />
                        <CardContent>
                          <Typography gutterBottom variant="h5">{pub.title}</Typography>
                          <Typography variant="body">{pub.message}</Typography>
                        </CardContent>
                      </CardActionArea>
                      <CardActions className={classes.card_action}>
                        <div className={classes.item}>
                          <IconButton size="small">
                            <ThumbUpAlt/>
                          </IconButton>
                          <IconButton size="small">
                            <FavoriteBorder/>
                          </IconButton>
                          <Typography variant="body2">48k likes</Typography>
                        </div>
                        <div className={classes.item}>
                          <Button className={classes.link} size="small" onClick={() => handleExpandClick(pub.id)}>2k Comments</Button>

                          <IconButton size="small" className={classes.right}>
                            <Share/>
                          </IconButton>
                        </div>
                      </CardActions>
                      <Collapse in={expanded[pub.id]} timeout="auto" unmountOnExit>
                        <CardContent>
                           <div className={classes.item}>
                            <Avatar src={current.avatar}/>&emsp;
                            <FormControl fullWidth>
                                  <Input
                                    placeholder="Your comments "
                                    multiline
                                    endAdornment={
                                    <InputAdornment position="end">
                                      <IconButton
                                        aria-label="toggle password visibility"
                                        edge="end"
                                        >
                                        <Telegram />
                                      </IconButton>
                                      </InputAdornment>
                                    }
                                  />
                              </FormControl>

                           </div>
                           <div className={classes.item}>
                              <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper', overflow: 'auto' }}>
                                <ListItem alignItems="flex-start">
                                  <ListItemAvatar>
                                    <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
                                  </ListItemAvatar>
                                  <ListItemText
                                    primary="Brunch this weekend?"
                                    secondary={
                                      <Fragment>
                                        <Typography
                                          sx={{ display: 'inline' }}
                                          component="span"
                                          variant="body2"
                                          color="text.primary"
                                        >
                                          Ali Connors
                                        </Typography>
                                        {" — I'll be in your neighborhood doing errands this…"}
                                      </Fragment>
                                    }
                                  />
                                </ListItem>
                                <Divider variant="inset" component="li" />
                              </List>
                           </div>    
                        </CardContent>
                      </Collapse>
                    </Card>
                  </div>
                )
              }
            })
            }
          </div>
        )
      })}
    </div>
  );
}
