import {useState,useEffect } from 'react';
import { Typography,makeStyles,Card,CardMedia
        ,Avatar,CardContent
        ,Grid,Button,ButtonGroup
        ,ImageList,ImageListItem, Paper, Box,Table,
        TableBody,TableCell,TableContainer,TableHead,TableRow } from '@material-ui/core';
import { Room,Person,Cake,Group,GroupAdd,Info } from '@material-ui/icons';
import {useCookies} from 'react-cookie';
import { useParams } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
  container:{
    display: "flex",
    border: "1px solid #ece7e7",
    top: theme.spacing(60),
    width: '53vw',
    position: 'absolute',
    padding: theme.spacing(1),
  },
  media:{
    height: 250,
    position: 'relative',
    top: theme.spacing(0),
    [theme.breakpoints.down("sm")]:{
      height: 150,
    },
  },
  large: {
    width: theme.spacing(20),
    height: theme.spacing(20),
    left: theme.spacing(40),
    top: theme.spacing(30),
    position: 'absolute',
    border: `2px solid ${theme.palette.background.paper}`,
    [theme.breakpoints.down("sm")]:{
      width: theme.spacing(10),
      height: theme.spacing(10),
      left: theme.spacing(21),
      top: theme.spacing(24),
    },
  },
  iconBtn: {
    width: theme.spacing(2),
    height: theme.spacing(2),
    top: theme.spacing(-11),
    border: `2px solid ${theme.palette.background.paper}`,
    background: theme.palette.background.paper,
    "&:hover": {
          background: theme.palette.background.paper,
    },
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-6),
    },
  },
  iconBtn2: {
    top: theme.spacing(-38),
    [theme.breakpoints.down("sm")]:{
      top: theme.spacing(-21),
    },
  },
  item:{
    display: "flex",
    alignItems: "center",
    marginTop: theme.spacing(0),
    [theme.breakpoints.up("sm")]:{
      marginTop: theme.spacing(1),
    },
  },
  icon:{
    marginRight: theme.spacing(1),
    [theme.breakpoints.up("sm")]:{
      fontSize: "18px",
    },
  },

  text:{
    fontWeight: 500,
    color: '#555',
    textTransform: 'capitalize',
    position: 'absolute',
    left: theme.spacing(65),
    marginTop: theme.spacing(2),
    [theme.breakpoints.down("sm")]:{
      left: theme.spacing(22),
      top: theme.spacing(33),
    },
  },
  paper:{
    color: '#555',
    textTransform: 'capitalize',
    position: 'absolute',
    width: 110,
    height: 100,
    left: theme.spacing(60),
    marginTop: theme.spacing(5),
    padding: theme.spacing(1),
    [theme.breakpoints.down("sm")]:{
      left: theme.spacing(16),
      top: theme.spacing(33),
    },
  },
  box_item:{
    display: 'flex',
    justifyContent: 'space-around',
    [theme.breakpoints.down("xs")]:{
      display: 'flex',
      justifyContent: 'space-around',
    },

  },
}));

export default function CardProfile() {
  const classes = useStyles();
  const [user, setUser] = useState([]);
  const { id } = useParams()




  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setUser(resp))
    .catch(error => console.log(error))

  }, [])

  function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
  }

  const rows = [
    createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
  ];

  return (
      <>
        <CardMedia className={classes.media} image={user.img_covert} />
        <Avatar src={user.avatar} className={classes.large} />
        <Typography className={classes.text}>
            @{user.username}
        </Typography>
        <Box
          sx={{
            display: 'flex',
            '& > :not(style)': {
              m: 1,
              width: '34vw',
              height: '5vh',

            },
          }}
        >
          <TableContainer className={classes.paper} style={{ minHeight: 90 }}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell align="center">Friends</TableCell>
                  <TableCell align="center">Followers</TableCell>
                  <TableCell align="center">Followings</TableCell>
                  <TableCell align="center">Posts</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {rows.map((row) => (
                  <TableRow
                    key={row.name}
                    sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                  >
                    <TableCell align="center">20k</TableCell>
                    <TableCell align="center">2k</TableCell>
                    <TableCell align="center">1k</TableCell>
                    <TableCell align="center">10</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Box>
        <div className={classes.container}>
          <Typography>
            Name
          </Typography>
        </div>
      </>
  );
}
