import React from 'react'
import { withRouter } from "react-router";
import { Badge,Avatar,IconButton } from "@material-ui/core";
import { FavoriteBorder,Favorite,ArrowBackIos,ShoppingCart,AddShoppingCart,Remove,Add } from "@material-ui/icons";
import axios from 'axios';
import apiService from '../../../APIService';
import { AuthContext } from "../../../context/AuthContext";
import "./style.css"

export class index extends React.Component {
	static contextType = AuthContext;
	constructor(props){
		super(props);
		this.id = this.props.match.params.id_prod;
		this.state={
			profile:[],
			number: 1,
		}

	}

	getCurrentUser = async() =>{
    let data = await axios({
      method: 'get',
      url: apiService.url+`users/${this.context.user.id}/`,

    }).then(({data}) => data)
    this.setState({profile: data})
  };

  minus=()=>{
  	if(this.state.number === 1){
  		return null
  	}else{
  		this.setState({number: this.state.number-1})
  	}
  }

  add=()=>{
  	if(this.state.number === 9){
  		return null
  	}else{
  		this.setState({number: this.state.number+1})
  	}
  }

  componentDidMount(){
    this.getCurrentUser()
  };

	render() {
		return (
			<div className="details-cont">
				<div className="nav">
					<IconButton onClick={this.props.history.goBack}><ArrowBackIos className="custom-basket"/></IconButton>
					<div className="rights">
						<IconButton
				            aria-label="Show basket contents"
				            color="inherit"
				         >
							<Badge badgeContent={2} color="secondary">
								<ShoppingCart className="custom-basket"/>
							</Badge>
						</IconButton>
						<Avatar src={this.state.profile.avatar}/>
					</div>
				</div>
				<div className="bottom">
					<div className="left">
						<div className="prod-pic">
							<img src="https://cdn.pixabay.com/photo/2017/03/14/17/24/women-2143800_960_720.jpg" alt="products" id/>
						</div>
						<div className="bottom-grid">
								<div className="item">
									<img src="https://cdn.pixabay.com/photo/2021/09/13/08/18/blue-flower-6620619_960_720.jpg" alt="products" />
								</div>

								<div className="item">
									<img src="https://cdn.pixabay.com/photo/2021/11/06/16/11/greece-6773683_960_720.jpg" alt="products" />
								</div>

								<div className="item">
									<img src="https://cdn.pixabay.com/photo/2017/03/31/15/35/leaves-2191649_960_720.jpg" alt="products" />
								</div>

								<div className="item">
									<img src="https://cdn.pixabay.com/photo/2021/10/16/06/13/girl-6714026_960_720.jpg" alt="products" />
								</div>
						</div>
					</div>
					<div className="right">
						<p>Categories/bags</p>
						<h2>Malagasy Bags by Orthencia</h2>
						<h4>Â£9.5</h4>
						<div className="input-numb">
							<div id="btn" onClick={this.minus}><Remove htmlColor="#ff5959"/></div>
							<div className="numb"><input type="text" className="input-text" pattern="[0-9]*" value={this.state.number}/></div>
							<div id="btn" onClick={this.add}> <Add htmlColor="#9dceff"/></div>
						</div>
						<h3>Poduct details</h3>
						<p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
						Sed non risus. Suspendisse lectus tortor, dignissim sit amet, adipiscing nec, ultricies sed, dolor.</p>
						<div className="btn-conf">
							<button id="btn1"><AddShoppingCart /></button>
							<button id="btn2"><FavoriteBorder /></button>
						</div>
					</div>
				</div>
			</div>
		)
	}
}

export default withRouter(index)
