import React from 'react';
import { withStyles,Avatar,Typography,InputBase } from '@material-ui/core';
import { MoreVert,Telegram,PermMedia,EmojiEmotions,Close,Videocam,PhotoCamera, } from '@material-ui/icons';
import axios from 'axios';
import {format} from 'timeago.js';

const useStyles = theme => ({
	container:{
		width: '350px',
		height: '450px',
		position: 'fixed',
		bottom:0,
		right: '30px',
		zIndex:2,
	},
	contItemBottom:{
		width: '100%',
		height: '80%',
		overflowY: 'auto',

	},
	itemTop:{
		width: '100%',
		height: '10%',
		alignItems: 'center',
		justifyContent: 'space-between',
		display: 'flex',
		textTransform: 'capitalize',
		color: "#848484",
		fontSize: "20px",
		fontWeight: 500,
		borderBottom: '1px solid #464646',
	},

	message:{
		display: 'flex',
		padding: '10px',
		textTransform: 'capitalize',
	},
	messageText:{
	    padding: '10px',
	    borderRadius: '20px',
	    backgroundColor: '#1877f2',
	    color: 'white',
	    maxWidth: '200px',
	},

	messageOwn:{
		display: 'flex',
		flexDirection: 'column',
		alignItems: 'flex-end',
		padding: '10px',
		textTransform: 'capitalize',
	},

	messageOwnTex:{
		padding: '10px',
	    borderRadius: '20px',
	    backgroundColor: '#666666',
	    color: 'black',
	    maxWidth: '200px',
	},

	messageBottom:{
	    fontSize: '8px',
	    alignItems: 'flex-end',
	},

	inputCustom:{
		width: '95%',
		height: '8%',
		textTransform: 'capitalize',
		backgroundColor: ' #464646',
		borderRadius: theme.spacing(3),
		display: 'flex',
		alignItems: 'center',
		color: "#ececec",
		paddingLeft: '10px',
		paddingRight: '10px',

	},
	input:{
	    color: "white",
	    marginLeft: theme.spacing(1),
	    width: "80%",
	},
	contItem:{
		width: "100%",
		height: "100%",
		borderTopLeftRadius: theme.spacing(2),
		borderTopRightRadius: theme.spacing(2),
		backgroundColor: '#3e3e3e',
	},

});
export class BoxMessage extends React.Component {
	constructor(props){
    	super(props);
    	this.refImg = React.createRef();
    	this.state={
    		message:'',
    		messages:[],
    		profile:[],
    		image: '',
    	};
    	this.scrollRef = React.createRef();
	};
	getProfile = async()=>{
		let data = await axios.get(`http://127.0.0.1:8000/userapp/users/${this.props.receiver}/`)
		.then(({data}) => data)
		this.setState({profile:data})
	};
	getMessage = async()=>{
		let data = await axios.get('http://127.0.0.1:8000/userapp/message/')
		.then(({data}) => data)
		this.setState({messages:data})
		console.log(this.state.messages)
		//this.setState({messages: this.state.messages.concat(this.state.message)})
		//this.setState({message: ''})
	};

	sendMessage = async(own,receiver,content)=>{
		let data = await axios.post('http://127.0.0.1:8000/userapp/message/',{sender:own,receiver:receiver,content:content})
		.then(({data}) => data)
		this.setState({messages: this.state.messages.concat(data)})
		this.setState({message: ''})
	};

	closeBox = () =>{
		this.props.open()
	};

	imageHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          this.setState({image:reader.result})
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      const base64 = await this.convertBase64(file);
      this.setState({image:base64})
    };

  convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);

        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
    };

	componentDidMount(){
	    this.getProfile()
	    this.getMessage()
	  };
	render() {
		const { classes } = this.props;
		return (
			<div className={classes.container}>
				<div className={classes.contItem}>
					<div className={classes.itemTop}>
						<div style={{display: 'flex',alignItems: 'center',paddingLeft: '5px',paddingBottom: '2px'}}>
							<Avatar src={this.state.profile.avatar}/>
							<Typography style={{marginLeft: '6px',}} >
								{this.state.profile.username}
							</Typography>
						</div>
						<div style={{display: 'flex',alignItems: 'center',paddingRight: '5px'}}>
							<Videocam style={{marginRight:'6px', cursor: 'pointer'}}/>
							<Close style={{cursor: 'pointer'}} onClick={this.closeBox}/>
						</div>
					</div>
					<div className={classes.contItemBottom}>
						{this.state.messages && this.state.messages.map(privateMessage=>{
							return(
								<div key={privateMessage.id}>
									<div className={(privateMessage.sender === this.props.own) ? classes.messageOwn : classes.message}>
										<div style={{display: 'flex'}}>
											{(privateMessage.sender === this.props.own) ? null : <Avatar src={this.state.profile.avatar} style={{marginRight: '5px'}} />}
											<div className={(privateMessage.sender === this.props.own) ? classes.messageOwnTex : classes.messageText}>
												<Typography > {privateMessage.content} </Typography>
												<div className={classes.messageBottom}>
													{format(privateMessage.createdAt)}
												</div>
											</div>
										</div>
									</div>
								</div>
							)
						})}

					</div>
					<div style={{padding:"10px"}}>
						<div className={classes.inputCustom}>
							<PhotoCamera style={{cursor:'pointer'}}/>
							<input type='file' id='file-img' onChange={(e) => {
				                this.imageHandler(e);
				              }} ref={this.refImg} style={{display: 'none'}}/>
							<EmojiEmotions style={{cursor:'pointer'}}/>
							<InputBase placeholder="Type anyphing..." className={classes.input}
								value = {this.state.message}
              					onChange = {e => this.setState({message:e.target.value})}
							/>
							<Telegram style={{cursor:'pointer'}} onClick={()=>this.sendMessage(this.props.own,this.state.profile.id,this.state.message)}/>
						</div>
					</div>
				</div>
			</div>
		)
	}
}

export default withStyles(useStyles)(BoxMessage)
