import React from 'react'
import Resizer from "react-image-file-resizer";
import './style.css'
export class index extends React.Component {
	constructor(props){
	    super(props);
	    this.refImg = React.createRef();
	    this.state={
	    	images: [],
	    	img: false,
	    }
	};

	imageHandler = async (e) => {
		const MAX_LENGTH = 5;
		if (Array.from(e.target.files).length > MAX_LENGTH) {
		    e.preventDefault();
		    alert(`Cannot upload files more than ${MAX_LENGTH}`);
		    return;
		  }else{
	      	let files = Array.from(e.target.files).map(file => {
	            let reader = new FileReader();
	            return new Promise(resolve => {
	                reader.onload = () => resolve(reader.result);
	                reader.readAsDataURL(file)

	            });

	        });
	        let res = await Promise.all(files);
	        this.setState({images:res, img:true})}
    };

  	addProd = async() =>{
  		console.log("ok")
  	}
	render() {
		return (
			<div className="manage">
				<div className="left">
					<div className="header"><span>&#8592;</span><span>Add</span></div><br/>
					<input type="text" name="" id="" required placeholder="Product name..."/>
					<textarea placeholder="Product description..."></textarea>
					<div className="price">
						<input type="text" placeholder="Price..." required pattern="[0-9]*"/>
						<input type="text" placeholder="Quantity in stock..." required pattern="[0-9]*"/>
					</div>
					<div className="pic" onClick={(event) => {
				                event.preventDefault();
				                this.refImg.current.click();
				                }}><span>&#10009;</span><small>Product pic</small></div>
					<input type='file' id='file-img' onChange={(e) => {
				                this.imageHandler(e);
				              }} ref={this.refImg} style={{display: 'none'}} multiple accept="image/*"/>
				    {this.state.img &&
					    <div className="images">
					    	{this.state.images.map(image =>{
					    		return(
									<img src={image} alt="pic" />
					    		)
					    	})

					    	}
					    </div>}
					<div className="action"><span></span><button>Add</button></div>
				</div>
				<div className="right"></div>
			</div>
		)
	}
}

export default index
