import {useState, useRef } from 'react';
import { Typography,Grid,makeStyles,Badge,IconButton, Avatar } from '@material-ui/core';
import { CameraAlt,Edit } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
  container:{
    display: "flex",
    border: "1px solid #ece7e7",
    height: '100vh',
  },
}));

export default function CardProfile(props) {
  const classes = useStyles();
  const inputFile = useRef(null);
  const inputFile2 = useRef(null);
  const [img1, setImg1] = useState('/broken-image.jpg');
  const [img2, setImg2] = useState('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRfRIXM7-2ET0SS_Z471QJwzBlkjqT4IepBCPXmlByfIIwViUiZAZtoMbAzyFHVNxQMKXs&usqp=CAU')

  const imageHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setImg1(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      const base64 = await convertBase64(file);
      setImg1(base64)
    };

  const imageCover = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          setImg2(reader.result)
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const fileCov = e.target.files[0];
      const base64 = await convertBase64(fileCov);
      setImg2(base64)
    };

  const convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);

        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
    };

  return (
    <Grid container component="main" className={classes.container}>

      <div className={classes.paper}>
          <div className={classes.profileCover}>
            <Badge
              overlap="circular"
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'right',
              }}
              badgeContent={
                <IconButton
                  edge="end"
                  onClick={(event) => {
                  event.preventDefault();
                  inputFile2.current.click();
                  }}
                 >
                  <Edit />
                </IconButton>
              }
            >
              <img
                className="profileCoverImg"
                src={img2}
                height="150"
                width= "100%"
              />
            </Badge>
          </div>
          <Badge
            overlap="circular"
            anchorOrigin={{
              vertical: 'bottom',
              horizontal: 'right',
            }}
            badgeContent={
              <IconButton
                edge="end"
                className={classes.iconBtn}
                onClick={(event) => {
                event.preventDefault();
                inputFile.current.click();
                }}
               >
                <CameraAlt />
              </IconButton>
            }
          >
            <Avatar src={img1} className={classes.large} />
          </Badge>

          <input type="file" accept="image/*" name="image-upload" id="input" ref={inputFile} style={{display: 'none'}}
            onChange={imageHandler}
          />
          <input type="file" accept="image/*" name="image-upload" id="input" ref={inputFile2} style={{display: 'none'}}
            onChange={imageCover}
          />
      </div>
    </Grid>

  );
}
