import React from 'react';
import { withStyles,Avatar,Typography,InputBase } from '@material-ui/core';
import { MoreVert,Telegram,PermMedia,EmojiEmotions,Close,Videocam,PhotoCamera, } from '@material-ui/icons';
import axios from 'axios';
const useStyles = theme => ({
	container:{
		width: '350px',
		height: '450px',
		position: 'fixed',
		bottom:0,
		right: '30px',
		zIndex:2,
	},
	contItemBottom:{
		width: '100%',
		height: '80%',
		overflowY: 'auto',

	},
	itemTop:{
		width: '100%',
		height: '10%',
		alignItems: 'center',
		justifyContent: 'space-between',
		display: 'flex',
		textTransform: 'capitalize',
		color: "#848484",
		fontSize: "20px",
		fontWeight: 500,
		borderBottom: '1px solid #464646',
	},

	message:{
		display: 'flex',
		padding: '10px',
		textTransform: 'capitalize',
	},
	messageText:{
	    padding: '10px',
	    borderRadius: '20px',
	    backgroundColor: '#1877f2',
	    color: 'white',
	    maxWidth: '200px',
	},

	messageOwn:{
		display: 'flex',
		flexDirection: 'column',
		alignItems: 'flex-end',
		padding: '10px',
		textTransform: 'capitalize',
	},

	messageOwnTex:{
		padding: '10px',
	    borderRadius: '20px',
	    backgroundColor: '#666666',
	    color: 'black',
	    maxWidth: '200px',
	},

	messageBottom:{
	    fontSize: '10px',
	    alignItems: 'flex-end',
	},

	inputCustom:{
		width: '95%',
		height: '8%',
		textTransform: 'capitalize',
		backgroundColor: ' #464646',
		borderRadius: theme.spacing(3),
		display: 'flex',
		alignItems: 'center',
		color: "#ececec",
		paddingLeft: '10px',
		paddingRight: '10px',

	},
	input:{
	    color: "white",
	    marginLeft: theme.spacing(1),
	    width: "80%",
	},
	contItem:{
		width: "100%",
		height: "100%",
		borderTopLeftRadius: theme.spacing(2),
		borderTopRightRadius: theme.spacing(2),
		backgroundColor: '#3e3e3e',
	},

});
export class BoxMessage extends React.Component {
	constructor(props){
    	super(props);
    	this.state={
    		message:'',
    		messages:[],
    		profile:[],
    	};
    	this.scrollRef = React.createRef();
	};
	getProfile = async()=>{
		let data = await axios.get(`http://127.0.0.1:8000/userapp/users/${this.props.receiver}/`)
		.then(({data}) => data)
		this.setState({profile:data})
	};
	getMessage = async()=>{
		let data = await axios.get('http://127.0.0.1:8000/userapp/message/')
		.then(({data}) => data)
		this.setState({messages: data})
		//this.setState({messages: this.state.messages.concat(this.state.message)})
		//this.setState({message: ''})
	};
	closeBox = () =>{
		this.props.open()
	};

	componentDidMount(){
	    this.getProfile()
	    this.getMessage()
	  };
	render() {
		const { classes } = this.props;
		return (
			<div className={classes.container}>
				<div className={classes.contItem}>
					<div className={classes.itemTop}>
						<div style={{display: 'flex',alignItems: 'center',paddingLeft: '5px',paddingBottom: '2px'}}>
							<Avatar src={this.state.profile.avatar}/>
							<Typography style={{marginLeft: '6px',}} >
								{this.state.profile.username}
							</Typography>
						</div>
						<div style={{display: 'flex',alignItems: 'center',paddingRight: '5px'}}>
							<Videocam style={{marginRight:'6px', cursor: 'pointer'}}/>
							<Close style={{cursor: 'pointer'}} onClick={this.closeBox}/>
						</div>
					</div>
					<div className={classes.contItemBottom}>
						{this.state.messages && this.state.messages.map((mp, index) =>{
							return(
								<div key={index}>
									<div className={classes.messageOwn}>
										<div>
											<div className={classes.messageOwnTex}>
												<Typography > {mp} </Typography>
												<div className={classes.messageBottom}>
													22:10
												</div>
											</div>
										</div>
									</div>
								</div>
							)
						})}

					</div>
					<div style={{padding:"10px"}}>
						<div className={classes.inputCustom}>
							<PhotoCamera style={{cursor:'pointer'}}/>
							<EmojiEmotions style={{cursor:'pointer'}}/>
							<InputBase placeholder="Type anyphing..." className={classes.input}
								value = {this.state.message}
              					onChange = {e => this.setState({message:e.target.value})}
							/>
							<Telegram style={{cursor:'pointer'}} onClick={this.getMessage}/>
						</div>
					</div>
				</div>
			</div>
		)
	}
}

export default withStyles(useStyles)(BoxMessage)
