import React,{ useState,useEffect } from 'react';
import { AppBar,makeStyles, Toolbar, Typography,InputBase,alpha,Badge,Avatar,IconButton,
        Menu,MenuItem,Divider,Box,ListItemIcon } from '@material-ui/core';
import { Mail,Notifications,Search,Cancel,Person,ExitToApp,PersonAdd,PersonRemove } from '@material-ui/icons';
import { useHistory } from 'react-router-dom';
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

const useStyles = makeStyles((theme) => ({
  LogoLg:{
    display: "none",
    [theme.breakpoints.up("sm")]:{
      display: "block"
    }
  },
  LogoSm:{
    display: "block",
    [theme.breakpoints.up("sm")]:{
      display: "none"
    }
  },
  toolbar:{
    display: "flex",
    justifyContent: "space-between"
  },
  search:{
    display: "flex",
    alignItems: "center",
    padding: theme.spacing(.3),
    paddingLeft: theme.spacing(1),
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    borderRadius: theme.spacing(1),
    width: "40%",
    [theme.breakpoints.down("xs")]:{
      display: (props) => props.open ? "flex" : "none",
      width : "70%",
    }
  },
  input:{
    color: "white",
    marginLeft: theme.spacing(1),
    width: "90%",
  },
  cancel:{
    [theme.breakpoints.up("sm")]:{
      display: "none",
    },
  },
  searchbtn:{
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]:{
      display: "none",
    }
  },
  icons:{
    alignItems: "center",
    display: (props) => props.open ? "none" : "flex",

  },
  badge:{
    margin: theme.spacing(1),
  },
  avatar:{
    marginRight: theme.spacing(1),
  },
  item:{
    spacing: 0,
  },
  title:{
    textDecoration: "none",
    color: "#555",
    textTransform: 'none',
  },
  second:{
    textDecoration: "none",
    color: "#555",
    textTransform: 'none',
    fontSize: 13,
  },

  menu:{
    position: 'fixed',
    minWidth: theme.spacing(30),
    top: '66px',
    right: '10px',
    background: "#f5f5f5",
    border: '1px solid #ece7e7',
    overflowY:'scroll',
    zIndex: 1,
    [theme.breakpoints.down("xs")]:{
      width: "100vw",
      height: "100vh",
      borderRadius: 0,
      top: '56px',
      right: 0,
      overflow:'hidden',
    }
  },
  menuItem:{
    display: 'flex',
    padding: '10px',
    color: 'dimgray',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  itmes:{
    marginLeft: '5px',
  },
}));
export default function NavBar() {
  const [open, setOpen] = useState(false);
  const classes = useStyles({ open });
  const [avatar, setAvatar] = useState([]);
  let history = useHistory();

  const [anchorEl, setAnchorEl] = useState(null);
  const [anchorElP, setAnchorElP] = useState(null);
  const isMenuOpenP = Boolean(anchorElP);
  const isMenuOpen = Boolean(anchorEl);
  const { user } = useContext(AuthContext);

  const handleProfileMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleProfileMenuOpenP = (event) => {
    setAnchorElP(event.currentTarget);
  };

  const handleMenuCloseP = () => {
    setAnchorElP(null);

  };

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${user.id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setAvatar(resp.avatar))
    .catch(error => console.log(error))

