import React,{ useState,useEffect } from 'react';
import { AppBar,makeStyles, Toolbar, Typography,InputBase,alpha,Badge,Avatar,IconButton,
        Menu,MenuItem,Divider,Box,ListItemIcon } from '@material-ui/core';
import { Mail,Notifications,Search,Cancel,Person,ExitToApp,PersonAdd,HighlightOff } from '@material-ui/icons';
import { useHistory } from 'react-router-dom';
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

const useStyles = makeStyles((theme) => ({
  LogoLg:{
    display: "none",
    [theme.breakpoints.up("sm")]:{
      display: "block"
    }
  },
  LogoSm:{
    display: "block",
    [theme.breakpoints.up("sm")]:{
      display: "none"
    }
  },
  toolbar:{
    display: "flex",
    justifyContent: "space-between"
  },
  search:{
    display: "flex",
    alignItems: "center",
    padding: theme.spacing(.3),
    paddingLeft: theme.spacing(1),
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    borderRadius: theme.spacing(1),
    width: "40%",
    [theme.breakpoints.down("xs")]:{
      display: (props) => props.open ? "flex" : "none",
      width : "70%",
    }
  },
  input:{
    color: "white",
    marginLeft: theme.spacing(1),
    width: "90%",
  },
  cancel:{
    [theme.breakpoints.up("sm")]:{
      display: "none",
    },
  },
  searchbtn:{
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]:{
      display: "none",
    }
  },
  icons:{
    alignItems: "center",
    display: (props) => props.open ? "none" : "flex",

  },
  badge:{
    margin: theme.spacing(1),
  },
  avatar:{
    marginRight: theme.spacing(1),
  },
  item:{
    spacing: 0,
  },
  title:{
    textDecoration: "none",
    color: "#555",
    textTransform: 'none',
  },
  second:{
    textDecoration: "none",
    color: "#555",
    textTransform: 'none',
    fontSize: 13,
  },

  menu:{
    position: 'fixed',
    minWidth: theme.spacing(30),
    top: '66px',
    right: 0,

    overflowY:'scroll',
    zIndex: 1,
    [theme.breakpoints.down("xs")]:{
      width: "100vw",
      height: "100vh",
      borderRadius: 0,
      top: '56px',
      right: 0,
      overflow:'hidden',
    }
  },
  menuItem:{
    padding: '10px',
    marginBottom: '5px',
    color: 'dimgray',
    alignItems: 'center',

    background: "#f5f5f5",
    border: '1px solid #ece7e7',
    borderRadius: theme.spacing(1),
    "&:hover": {
      background: '#ece7e7',
    },
  },

  menuItemChild:{
    display: 'flex',
    marginBottom: '5px',
    justifyContent: 'space-between',
    "&:hover": {
      background: '#ece7e7',
    },
  },

  itmes:{
    marginLeft: '5px',
  },
}));
export default function NavBar() {
  const [open, setOpen] = useState(false);
  const [openM, setOpenM] = useState(false);
  const classes = useStyles({ open });
  const [avatar, setAvatar] = useState([]);
  let history = useHistory();


  const { user } = useContext(AuthContext);

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${user.id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setAvatar(resp.avatar))
    .catch(error => console.log(error))

  }, [user.id])



  const logout = () => {
    localStorage.removeItem("user")
    history.push('/')

  };


  return (
    <Box sx={{ flexGrow: 1, position: "relative" }}>
      <AppBar position="fixed">
          <Toolbar className={classes.toolbar}>
            <Typography variant="h6" className={classes.LogoLg}>
              Wanwork
            </Typography>
            <Typography variant="h6" className={classes.LogoSm}>
              Ww
            </Typography>
            <div className={classes.search}>
              <Search />
              <InputBase placeholder="Search..." className={classes.input}/>
              <Cancel className={classes.cancel} onClick={() => setOpen(false)} />
            </div>
            <div className={classes.icons}>
              <Search className={classes.searchbtn} onClick={() => setOpen(true)}/>
              <IconButton
                size="large"
                color="inherit"
                onClick={() => setOpenM(!openM)}
              >
                <Badge badgeContent={4} color="secondary">
                  <Person />
                </Badge>
              </IconButton>
              <IconButton
                size="large"
                color="inherit"
              >
                <Badge badgeContent={4} color="secondary">
                  <Mail />
                </Badge>
              </IconButton>
              <IconButton
                size="large"
                color="inherit"
              >
                <Badge badgeContent={2} color="secondary">
                  <Notifications />
                </Badge>
              </IconButton>
              <div onClick={() => setOpenM(!openM)} style={{cursor: 'pointer'}}>
                <Avatar src={avatar}/>
              </div>
            </div>
          </Toolbar>
      </AppBar>
      {openM && (
        <div className={classes.menu}>
          <div className={classes.menuItem}>
            <div className={classes.menuItemChild}>
              <div style={{display: 'flex'}}>
                <Avatar />
                <div className={classes.itmes}>
                  <Typography style={{fontSize: '16px',fontWeight: 'bold'}}>
                    Manohisoa
                  </Typography>
                  <Typography style={{fontSize: '12px',}}>
                    Manohisoa
                  </Typography>
                </div>
              </div>
              <div style={{display: 'flex'}}>
                <PersonAdd htmlColor="green" style={{cursor: 'pointer'}}/>
                <HighlightOff htmlColor="tomato" style={{marginLeft: '5px',cursor: 'pointer'}}/>
              </div>
            </div>

          </div>
        </div>
      )}
    </Box>
  );
}
