import React, {Fragment} from 'react'
import { Card,CardHeader,CardActionArea,
        CardMedia,CardContent, Typography,
        CardActions,withStyles, 
        Avatar,IconButton,Collapse,Button,
        InputAdornment,FormControl,Input,List,
        ListItem,ListItemText,ListItemAvatar,
        CircularProgress } from '@material-ui/core';
import { MoreVert,FavoriteBorder,Share,Telegram,PermMedia,Room,EmojiEmotions } from '@material-ui/icons';
import {format} from 'timeago.js';
import { Link } from 'react-router-dom';
import NumericLabel from 'react-pretty-numbers';
import axios from 'axios';
import { AuthContext } from "../context/AuthContext";
import InfiniteScroll from 'react-infinite-scroll-component';
import _ from 'lodash';


const useStyles = theme => ({
  media:{
    height: 250,
    [theme.breakpoints.down("sm")]:{
      height: 150,
    },
  },
  card:{
    marginBottom: theme.spacing(1),
    border: '1px solid #ece7e7',
    boxShadow: 'none',
  },
  tile:{
    height: theme.spacing(40),
    display: "flex",
    flexDirection: "column",
    margin: 5,

  },
  name:{
    fontSize: 16,
    fontWeight: 500,
    color: "#555",
  },
  card_action:{
    display: "flex",
    justifyContent: "space-between"
  },
  card_header:{
    background: "#f5f5f5",
  },
  item:{
    display: "flex",
    alignItems: "center",
    spacing: theme.spacing(2),
    color: "#555",
  },
  link:{
    display: "flex",
    textDecoration: "none",
    color: "#555",
    textTransform: 'capitalize',
  },
  share:{
    border: '1px solid #ece7e7',
    marginBottom: theme.spacing(1),
    padding: theme.spacing(2),
    borderRadius: theme.spacing(1),
    //boxShadow: '0px 1px 7px 0px rgba(0,0,0,0.37)',
  },
  share_top:{
    display: 'flex',
    alignItems: 'center',
  },
  shareBottom:{
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingTop: '10px'
  },
  options:{
    display: 'flex',
    marginLeft: theme.spacing(6.2),
  },
  option:{
    display: 'flex',
    alignItems: 'center',

    padding: theme.spacing(1),
    cursor: 'pointer',
    "&:hover": {
          background: '#ece7e7',
          borderRadius: theme.spacing(1),
    },
    [theme.breakpoints.down("xs")]:{
      marginRight: '2px',
    },
  },
  shareIcon:{
    fontSize: '18px',
    marginRight: '3px',
  },
  shareOptionText:{
    fontSize: '12px',
    fontWeight: '500',
    color: "#555",
    [theme.breakpoints.down("sm")]:{
      display: 'none',
    },
  },
  shareButton:{
    textTransform: 'none',
    background: 'green',
    color: 'white',
    fontWeight: '500',
    "&:hover": {
          background: 'green',
          fontWeight: 'bold',
    },
    [theme.breakpoints.down("xs")]:{
      width: '45px',
      height: '30px',
      fontSize: '13px',
      padding: '3px',
    },
  },
  loading:{
    width: '100%',
    justifyContent: 'center',
  },
});



export class Post extends React.Component {
  static contextType = AuthContext;
  constructor(props){
    super(props);
    this.refImg = React.createRef();
    this.state={
            expanded:false,
            anchorElP: '',
            comment: '',
            share: '',
            comments: [],
            publication: [],
            users: [],
            current: [],
            next_url: 'http://127.0.0.1:8000/userapp/publication/',
            count: null,
            more_exist: true,
            image: '',
        };

  };

  handleExpandClick = (id) => {
    this.setState({ [`expanded_${id}`]:  _.isUndefined(this.state[`expanded_${id}`])?true:!this.state[`expanded_${id}`] });
  };

  handleProfileMenuOpenP = (event) => {
    this.setState({anchorElP:event.currentTarget});
  };

  handleMenuCloseP = () => {
    this.setState({anchorElP:null});

  };
  getPub = async() =>{
   await axios({
        method: 'get',
        url: this.state.next_url,
        headers:{
          Authorization: `Token ${this.context.user.token}`,
        },
      }).then(res =>{
        var has_more = false
        if(res.data.next){
          has_more = true
        }
        this.setState({
          next_url: res.data.next,
          count: res.data.count,
          publication: this.state.publication.concat(res.data.results),
          more_exist: has_more,
        })
        console.log(res.data.results)
    })
  };
  getCom = async() =>{
      let data = await axios({
        method: 'get',
        url: 'http://127.0.0.1:8000/userapp/comment/',
      }).then(({data}) => data)
      this.setState({comments: data})
  };
  getUser = async() =>{
    let data = await axios({
        method: 'get',
        url: 'http://127.0.0.1:8000/userapp/users/',
    }).then(({data}) => data)
    this.setState({users: data})
  };
  getCurrentUser = async() =>{
    let data = await axios({
      method: 'get',
      url: `http://127.0.0.1:8000/userapp/users/${this.context.user.id}/`,

    }).then(({data}) => data)
    this.setState({current: data})
  };

  btnComment = async(content,id_post) => {
    let author = parseInt(this.context.user.id, 10)
    let post_connected = parseInt(id_post, 10)
    const contents = {content:content,author:author,post_connected:post_connected}
    await axios.post('http://127.0.0.1:8000/userapp/comment/',contents)

    this.getCom()
    this.setState({comment:''})

    };

  btnShare = async()=>{
    let author = parseInt(this.context.user.id, 10)
    const contents = {user:author,message: this.state.share,image:this.state.image}
    await axios.post('http://127.0.0.1:8000/userapp/publication/',contents,{
      headers:{
        Authorization: `Token ${this.context.user.token}`
      }
    }).then(res=>{
      console.log(res)
    })
    this.setState({share:'',image:''})
  };

  fetchData = async() =>{
    await axios({
        method: 'get',
        url: this.state.next_url,
        headers:{
          Authorization: `Token ${this.context.user.token}`,
        },
      }).then(res =>{
        var has_more = false
        if(res.data.next){
          has_more = true
        }
        this.setState({
          next_url: res.data.next,
          count: res.data.count,
          publication: this.state.publication.concat(res.data.results),
          more_exist: has_more,
        })
    })
  };

  imageHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          this.setState({image:reader.result})
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      const base64 = await this.convertBase64(file);
      this.setState({image:base64})
    };

  convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);

        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
    };

  componentDidMount(){
    this.getUser()
    this.getPub()

    this.getCom()
    this.getCurrentUser()
  };
  render() {
    const { classes } = this.props;
    return (
      <InfiniteScroll
        dataLength={this.state.more_exist} //This is important field to render the next data
        next={this.fetchData}
        hasMore={!isLoading}
        loader={<p>Loading...</p>}
        endMessage={
          <p style={{ textAlign: 'center' }}>
            <b>Yay! You have seen it all</b>
          </p>
        }
        // below props only if you need pull down functionality
        pullDownToRefreshContent={
          <h3 style={{ textAlign: 'center' }}>&#8595; Pull down to refresh</h3>
        }
        releaseToRefreshContent={
          <h3 style={{ textAlign: 'center' }}>&#8593; Release to refresh</h3>
        }
      >
        <div className={classes.share}>
          <div className={classes.share_top}>
            <Avatar src={this.state.current.avatar} style={{marginRight: 10}}/>
            <Input
              placeholder="Share your advice ... "
              multiline
              fullWidth
              value = {this.state.share}
              onChange = {e => this.setState({share:e.target.value})}
            />
          </div>
          <div className={classes.shareBottom}>
            <div className={classes.options}>
              <div className={classes.option} onClick={(event) => {
                event.preventDefault();
                this.refImg.current.click();
                }}>
                <PermMedia htmlColor="tomato" className={classes.shareIcon}/>
                <span className={classes.shareOptionText}>Photo / video</span>
              </div>
              <input type='file' id='file-img' onChange={(e) => {
                this.imageHandler(e);
              }} ref={this.refImg} style={{display: 'none'}}/>

              <div className={classes.option}>
                <Room htmlColor="green" className={classes.shareIcon}/>
                <span className={classes.shareOptionText}>Location</span>
              </div>

              <div className={classes.option}>
                <EmojiEmotions htmlColor="goldenrod" className={classes.shareIcon}/>
                <span className={classes.shareOptionText}>Feellings</span>
              </div>
            </div>
            <Button className={classes.shareButton} onClick={this.btnShare}>Share</Button>
          </div>
        </div>
        {this.state.publication && this.state.publication.map(pub => {
          return(
          <div key = {pub.id}>
            {this.state.users && this.state.users.map(profile => {
              if (pub.user === profile.id){
                //const id_pub = pub.user
                //const id_aut = this.state.current.id
                return(
                  <div key = {profile.id}>
                    <Card className={classes.card}>
                      <CardHeader 
                        className={classes.card_header}
                        avatar={
                          <Avatar src={profile.avatar}/>
                        }
                        action={
                          <IconButton>
                            <MoreVert/>
                          </IconButton>
                        }
                        title={
                          <Link exact to='#' className={classes.link}>
                            <Typography className={classes.name}> {profile.username}</Typography>
                          </Link>
                        }
                        subheader={format(pub.date)}
                      />
                      <CardActionArea>
                        <CardMedia className={classes.media} image={pub.image} />
                        <CardContent>
                          <Typography variant="h6">{pub.message}</Typography>
                        </CardContent>
                      </CardActionArea>
                      <CardActions className={classes.card_action}>
                        <div className={classes.item}>.
                          <IconButton size="small" >
                            <FavoriteBorder htmlColor="pink"/>
                          </IconButton>
                          <Button className={classes.link} size="small" variant="text" disabled><NumericLabel params={{shortFormat: true,}}>{pub.like_nbr}</NumericLabel>&nbsp; Like</Button>
                        </div>
                        <div className={classes.item}>
                          <Button className={classes.link} size="small" onClick={() => this.handleExpandClick(pub.id)}
                          aria-expanded={this.state[`expanded_${pub.id}`] || false}
                          ><NumericLabel params={{shortFormat: true,}}>{pub.comments_nbr}</NumericLabel>&nbsp; Comment</Button>

                          <IconButton size="small" className={classes.right}>
                            <Share/>
                          </IconButton>
                        </div>
                      </CardActions>
                      <Collapse in={this.state[`expanded_${pub.id}`] || false} timeout="auto" unmountOnExit>
                        <CardContent>
                           <div className={classes.item}>
                            <Avatar src={this.state.current.avatar}/>&emsp;
                            <FormControl fullWidth>
                                  <Input
                                    placeholder="Your comments "
                                    multiline
                                    value = {this.state.comment}
                                    onChange = {e => this.setState({comment:e.target.value})}
                                    endAdornment={
                                    <InputAdornment position="end">
                                      <IconButton
                                        aria-label="toggle password visibility"
                                        edge="end"
                                        onClick={() => this.btnComment(this.state.comment,pub.id)}
                                        >
                                        <Telegram />
                                      </IconButton>
                                      </InputAdornment>
                                    }
                                  />
                              </FormControl>
                            </div>
                            <div style={{maxHeight: 150,overflowY: "scroll",}}>
                              {this.state.users && this.state.users.map(author => {
                                return(
                                  <div key={author.id}>
                                    {this.state.comments && this.state.comments.map(post_com => {
                                      if (author.id === post_com.author && post_com.post_connected === pub.id){
                                        return(
                                          <div key={post_com.id}>
                                            <div className={classes.item}>
                                              <List sx={{ width: '100%', bgcolor: 'background.paper', overflow: 'auto' }}>
                                                <ListItem alignItems="flex-start">
                                                  <ListItemAvatar>
                                                    <Avatar src={author.avatar} />
                                                  </ListItemAvatar>
                                                  <ListItemText
                                                    primary=<Link className={classes.link}>{author.username}</Link>
                                                    secondary={
                                                      <Fragment>
                                                      <Typography
                                                        sx={{ display: 'inline' }}
                                                        component="span"
                                                        variant="body2"
                                                        color="text.primary"
                                                      >
                                                        {post_com.content}
                                                      </Typography>
                                                      {" â€” ("+format(post_com.date_posted)+")"}
                                                      </Fragment>
                                                    }
                                                    />
                                                </ListItem>
                                              </List>
                                            </div>
                                          </div>
                                        )
                                      }else return null
                                    })}
                                  </div>
                                )
                              })}
                            </div>
                          </CardContent>
                      </Collapse>
                    </Card>
                  </div>
                )}else return null
            })}

          </div>)
        })}
      </InfiniteScroll>
    )
  }
}

export default withStyles(useStyles)(Post);
