import React from 'react'
import { Home,People,List,Image,PlayCircleOutline,Settings,LocalMall,Forum,
        WbSunny,Menu,Search,Brightness4,Notifications,Mail,Person,ExitToApp,ArrowRight,
        ArrowLeft,Lock,Brightness2 } from '@material-ui/icons';
import { connect } from "react-redux";
import { Route,Switch,withRouter,Link } from 'react-router-dom';
import { publicRequest,userRequest } from '../../requestMethods';
import	Post from '../Post'
import Profile from '../Profile'
import Messenger from '../Messenger'
import { useQuery } from "react-query";
import _ from 'lodash';
import './style.css'

function Query(props) {
  return (props.children(useQuery(props.keyName, props.fn, props.options)));
}


export class index extends React.Component {
	constructor(props){
		super(props);

		this.state={
			theme: '',
			profile:[],
			friends: [],
	    profiles: [],
	    user: [],
	    friend:{},
	    conversation: false,
	    messages: [],
	    private_message:[],
	    open: false,
	    checked: false,
		};

		this.handler = this.handler.bind(this);
	}

	handler(friend) {
		this.setState({friend:friend,private_message:[],conversation:true})
		this.getMessage(friend.id)
  }

  getMessage = async(id) =>{
  	let data = await publicRequest.get('userapp/message/')
  	.then(({data}) => data)
    this.setState({messages: _.sortBy(data.results, "id")})
    //await this.state.messages.filter(item=> (item.sender === parseInt(id, 10) && item.receiver=== parseInt(this.id, 10)) || (item.receiver === parseInt(id, 10) && item.sender=== parseInt(this.id, 10))).map(checked=>(this.setState({private_message:checked})))
    await this.state.messages && this.state.messages.map((item) =>{
    	if ((item.sender === parseInt(id, 10) && item.receiver=== parseInt(this.props.user.id, 10)) || (item.receiver === parseInt(id, 10) && item.sender=== parseInt(this.props.user.id, 10))){
    		this.setState({private_message:this.state.private_message.concat(item)})
    	}
    })
  };

	styleElement = () =>{
		let toggle = document.querySelector('.toggles')
		let navigation = document.querySelector('.navigations')
		let main = document.querySelector('.mains')

		navigation.classList.toggle('active')
		main.classList.toggle('active')
	}

	subMenu = () =>{
		let  menuBar = document.querySelector('.menu-bar')
		let  menuSettings = document.querySelector('.menu-settings')
		menuBar.style.marginLeft = "-250px";
		setTimeout(() =>{
			menuSettings.style.display = "block";
		}, 100)
	}

	submenuReturn = () =>{
		let  menuBar = document.querySelector('.menu-bar')
		let  menuSettings = document.querySelector('.menu-settings')

		menuBar.style.marginLeft = "0";
		menuSettings.style.display = "none";
	}

  changeTheme = async () =>{
  		if(this.state.profile.theme == null){
				if(this.state.checked){
					let data = await publicRequest.post('userapp/theme/',{id:this.props.user.id,theme:'light'})
					.then(({data}) => data)
		  		this.setState({theme: data.theme, checked: false})
		  	}else{
		  		let data = await publicRequest.post('userapp/theme/',{id:this.props.user.id,theme:'dark'})
					.then(({data}) => data)
		  		this.setState({theme: data.theme, checked: true})
		  	}
  		}else{
  			if(this.state.profile.theme[0] == "dark"){
					let data = await publicRequest.patch(`userapp/theme/${this.props.user.id}/`,{theme:'light'})
					.then(({data}) => data)
			  	this.setState({theme: data.theme, checked: false})
			  	this.getCurrentUser()

			  }else {
			  	let data = await publicRequest.patch(`userapp/theme/${this.props.user.id}/`,{theme:'dark'})
					.then(({data}) => data)
			  	this.setState({theme: data.theme, checked: true})
			  	this.getCurrentUser()
			  }
  		}

  }

  getCurrent = async() =>{
		let data;
		await publicRequest.get(`userapp/users/${this.props.user.id}`).then((res) => (data = res.data))
		return data;
	}

  getCurrentUser = async() =>{
    let data = await publicRequest.get(`userapp/users/${this.props.user.id}/`)
    .then(({data}) => data)
    this.setState({profile: data})
    if(this.state.profile.theme[0] == ''){
		  	if (this.defaultDark){
		  		this.setState({theme: 'dark', checked: true})
		  	}
		  }else{
		  	if(this.state.profile.theme[0] == "dark"){
			  	this.setState({theme: this.state.profile.theme[0], checked: true})	
			  }else {
			  	this.setState({theme: this.state.profile.theme[0], checked: false})
			  }
		  }
  };

  getProfile = async() =>{
		let data = await publicRequest.get(`userapp/users/${this.props.match.params.id}`)
		.then(({data}) => data)
		this.setState({profile: data})
	}

  getUser = async() =>{
    let data = await publicRequest.get('userapp/users/')
    .then(({data}) => data)
    this.setState({profiles: data})
  };

  getFriend = async() =>{
    let data = await publicRequest.get('userapp/friend/')
    .then(({data}) => data)
    this.setState({friends: data})
  };

  logout = () => {
    localStorage.clear()
    window.location.reload(false);
  };

  openBox = () =>{
  	this.setState({open: !this.state.open})
  }

  componentDidMount(){

	  	this.getCurrentUser()
	  	this.getUser()
    	this.getFriend()
	 };
	render() {
		return (
			<div className="home" data-theme={this.state.theme}>
				<div className="navigations">
					<ul>
						<li>
							<Link to={this.props.match.url} className="link">
								<div className="ahref">
									<span className="icon"><Home /></span>
									<span className="title"> WanWork</span>
								</div>
							</Link>
						</li>
						<li>
							<div className="ahref">
								<span className="icon"><People /></span>
								<span className="title"> Friends</span>
							</div>
						</li>
						<li>
							<div className="ahref">
								<span className="icon"><List /></span>
								<span className="title"> Lists</span>
							</div>
						</li>
						<li>
							<div className="ahref">
								<span className="icon"><Image /></span>
								<span className="title"> Image Gallery</span>
							</div>
						</li>
						<li>
							<div className="ahref" onClick={this.teste}>
								<span className="icon"><PlayCircleOutline /></span>
								<span className="title"> Videos</span>
							</div>
