import React from 'react'
import { connect } from "react-redux";
import { Route,Switch,withRouter,Link } from 'react-router-dom';
import { publicRequest,userRequest } from '../../requestMethods';
import Info from '../Info'
import { useQuery } from "react-query";
import './style.css'

function Query(props) {
  return props.children(useQuery(props.keyName, props.fn, props.options));
}

export class index extends React.Component {
	constructor(props){
		super(props);
		this.state={
			friends: [],
	    	profile: {},
		};
	}

	getProfile = async() =>{
		let data = await publicRequest.get(`userapp/users/${this.props.match.params.id}`)
		.then(({data}) => data)
		this.setState({profile: data})
	}

	getFriends = async() =>{
		await publicRequest.get('userapp/users/').then(res => res.data)
	}
	getFriend = async() =>{
		let data = await publicRequest.get('userapp/friend/')
		.then(({data}) => data)
		this.setState({friends: data})
	}

	checkFriend = (userId,friendId) =>{
	    if (this.state.friends.filter(item=> item.user == userId && item.friend == friendId).length == 0)
	      return false
	    else return true
	};


	render() {
		return (
			<div>
				<Query
					keyName="users"
					fn={() => this.getFriends()}
				>
					{({ data, isLoading, onError }) => {
	          if (isLoading) return <h1>Loading</h1>;
	          if (onError) return <h1>Error</h1>;
	          return (
	            <div>
	            	{data}
	            </div>
	          );
	        }}
				</Query>
			</div>
		)
	}
}

const mapStateToProps = (state) => ({
  user: state.user.currentUser,
});
export default connect(mapStateToProps,null)(withRouter(index))
