import React from 'react'
import { Search,Image,EmojiEmotions,AttachFile,
				ThumbUp,Telegram,Videocam,Call,MoreVert, 
				Menu, Notifications} from '@material-ui/icons';
import { connect } from "react-redux";
import ScrollToBottom from 'react-scroll-to-bottom';
import { CircularProgress } from '@material-ui/core';
import { Route,Switch,withRouter,Link } from 'react-router-dom';
import { publicRequest,userRequest } from '../../requestMethods';
import './style.css'
export class index extends React.Component {
	constructor(props){
		super(props);
		this.refImg = React.createRef();
	  this.refFl = React.createRef();
	  this.state={
	    message:'',
	    messages: [],
	    chosenEmoji: null,
	    open: false,
	    last_message: [],
	    doc_file: '',
	    image: '',
	    sending: false,
	    profile: [],
	    currentUser: [],
	  };
	}

	getProfile = async() =>{
		let data = await publicRequest.get('userapp/users/')
		.then(({data}) => data)
		this.setState({profile: data})	
	}

	getCurrent = async() =>{
		let data = await publicRequest.get(`userapp/users/${this.props.user.id}`)
		.then(({data}) => data)
		this.setState({currentUser: data})	
		//console.log(this.state.currentUser.friends)
	}

	imageHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          this.setState({image:reader.result})
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      if (file.size > 3e6) {
		      window.alert("Please upload a file smaller than 3 MB");
		      return false;
		    }else{
		      const base64 = await this.convertBase64(file);
		      this.setState({image:base64})}
    };

  docHandler = async (e) => {

      const reader = new FileReader();
      reader.onload = () =>{
        if(reader.readyState === 2){
          //this.setState({image:reader.result})
          console.log('file readed')
        }
      }
      reader.readAsDataURL(e.target.files[0])
      const file = e.target.files[0];
      if (file.size > 3e6) {
		      window.alert("Please upload a file smaller than 3 MB");
		      return false;
		    }else {
		      const base64 = await this.convertBase64(file);
		      this.setState({doc_file:base64})}
  };

  convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);

        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
  };

  sendMessage = async() =>{
  	try{
  		this.setState({seding: true})
  		if(this.state.image == '' && this.state.doc_file == ''){
		  	let data = await publicRequest.post('userapp/message/',{sender:this.props.user.id,receiver:this.props.friend.id,content:this.state.message})
		  	.then(({data}) => data)
		  	this.setState({message:'',sending:false})
		  	this.props.private_message.concat(data)
		  }else if (this.state.image !== '' && this.state.doc_file == ''){
		  	let data = await publicRequest.post('userapp/message/',{sender:this.props.user.id,receiver:this.props.friend.id,content:this.state.message,img:this.state.image})
		  	.then(({data}) => data)
		  	this.setState({message:'',sending:false, image:''})
		  	this.props.private_message.concat(data)
		  }else if (this.state.doc_file !== '' && this.state.image == ''){
		  	let data = await publicRequest.post('userapp/message/',{sender:this.props.user.id,receiver:this.props.friend.id,content:this.state.message,files:this.state.doc_file})
		  	.then(({data}) => data)
		  	this.setState({message:'',sending:false, doc_file:''})
		  	this.props.private_message.concat(data)
		  }else{
		  	let data = await publicRequest.post('userapp/message/',{sender:this.props.user.id,receiver:this.props.friend.id,content:this.state.message,img:this.state.image,files:this.state.doc_file})
		  	.then(({data}) => data)
		  	this.setState({message:'',sending:false, image:'', doc_file:''})
		  	this.props.private_message.concat(data)
		  }

  	}catch(error){
    		throw new Error(error);
    }
  }

	componentDidMount(){
	  	this.getProfile()
	  	this.getCurrent()
	 };
	render() {
		return (
			<div className="messenger">
				<div className='topBar'>
					<div className="toggles" onClick={this.props.styleElement}> <Menu /> </div>

					<div className="main-right">
						<div className="topbarStyle">
							<Notifications/>
						</div>
						<div className="topbarStyle">
							<img src={this.state.profile.avatar} alt="avatar" />
						</div>
					</div>
				</div>
				<div className="central">
					<div className="left">
						<div className="top">
							<div className="profile">
								<img src="{this.props.friend.avatar}" alt="avatar" />
								<div>
									<span>Erwan</span>
									<small>Online</small>
								</div>	
							</div>
							<div className="right-action">
								<Videocam className="icon"/>
								<Call className="icon"/>
								<MoreVert className="icon"/>
							</div>
						</div>

						<div className="middle">

						</div>

						<div className="bottom">
							<div className="bot-icons">
								<div className="topbarStyle">
									<Image onClick={(event) => {
					                event.preventDefault();
					                this.refImg.current.click();
					                }}/>
								</div>
								<input type='file' onChange={(e) => {
						                this.imageHandler(e);
						              }} ref={this.refImg} style={{display: 'none'}} accept=".png, .jpg, .jpeg"/>

						    <input type='file'  onChange={(e) => {
						                this.docHandler(e);
						              }} ref={this.refFl} style={{display: 'none'}} accept=".pdf"/>
								<div className="topbarStyle">
									<AttachFile onClick={(event) => {
					                event.preventDefault();
					                this.refFl.current.click();}}/>
								</div>
								<div className="topbarStyle">
									<EmojiEmotions />
								</div>
							</div>
							<>
								<textarea placeholder="Enter your message ..."
								value = {this.state.message}
								onChange = {e => this.setState({message:e.target.value})}></textarea>
								{this.state.message ==='' ?(
									<div className="topbarStyle">
										 <ThumbUp />
									</div>) : (
									<div className="topbarStyle" onClick = {this.sendMessage}>
										{this.state.sending ? <CircularProgress /> : <Telegram />}
									</div>
								)}
							</>
						</div>

					</div>
					<div className="right-bar">
						<div className="right-top">
							<div className="online">	
								<div className="friend">
										<div className="avatar">
											<img src="https://cdn.pixabay.com/photo/2022/02/14/08/53/woman-7012726_960_720.jpg" alt="" />
											<span></span>
										</div>
								</div>
								<div className="friendText" onClick={() => this.handler(1)}>
									<span>Erwan</span>
									<small>Online</small>
								</div>
							</div>
						</div>
						<div className="right-bottom">
							{this.state.currentUser.friends && this.state.currentUser.friends.map(friend =>{
								this.state.profile && this.state.profile.map(user =>{
									if(user.id === friend.friend_id){
										return(
											<div className="chat" key={friend.id}>	
												<div className="friend">
														<div className="avatar">
															<img src={user.avatar} alt="avatar" />
															<span></span>
														</div>
												</div>
												<div className="friendText" onClick={() => this.handler(1)}>
													<span>{user.username}</span>
													<small>Last messages</small>
												</div>
											</div>
										)
									}else return null
								})
							})}
						</div>
					</div>
				</div>
			</div>
		)
	}
}

const mapStateToProps = (state) => ({
  user: state.user.currentUser,
});
export default connect(mapStateToProps,null)(withRouter(index))
