import { useState,useEffect } from 'react';
import { AppBar,makeStyles, Toolbar, Typography,InputBase,alpha,Badge,Avatar,IconButton,
        Menu,MenuItem,Divider,Box } from '@material-ui/core';
import { Mail,Notifications,Search,Cancel,Person } from '@material-ui/icons';
import {useCookies} from 'react-cookie';

const useStyles = makeStyles((theme) => ({
  LogoLg:{
    display: "none",
    [theme.breakpoints.up("sm")]:{
      display: "block"
    }
  },
  LogoSm:{
    display: "block",
    [theme.breakpoints.up("sm")]:{
      display: "none"
    }
  },
  toolbar:{
    display: "flex",
    justifyContent: "space-between"
  },
  search:{
    display: "flex",
    alignItems: "center",
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    borderRadius: theme.shape.borderRadius,
    width: "40%",
    [theme.breakpoints.down("xs")]:{
      display: (props) => props.open ? "flex" : "none",
      width : "70%",
    }
  },
  input:{
    color: "white",
    marginLeft: theme.spacing(1)
  },
  cancel:{
    [theme.breakpoints.up("sm")]:{
      display: "none",
    },
  },
  searchbtn:{
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]:{
      display: "none",
    }
  },
  icons:{
    alignItems: "center",
    display: (props) => props.open ? "none" : "flex",

  },
  badge:{
    margin: theme.spacing(1),
  },
  avatar:{
    marginRight: theme.spacing(1),
  },
  item:{
    spacing: 0,
  },
  title:{
    textDecoration: "none",
    color: "#555",
    textTransform: 'none',
  },
  second:{
    textDecoration: "none",
    color: "#555",
    textTransform: 'none',
    fontSize: 13,
  },
}));
export default function NavBar() {
  const [open, setOpen] = useState(false);
  const classes = useStyles({ open });
  const [avatar, setAvatar] = useState([]);
  const [token] = useCookies(['mytoken']);

  const [anchorEl, setAnchorEl] = useState(null);
  const [anchorElP, setAnchorElP] = useState(null);
  const isMenuOpenP = Boolean(anchorElP);
  const isMenuOpen = Boolean(anchorEl);

  const handleProfileMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleProfileMenuOpenP = (event) => {
    setAnchorElP(event.currentTarget);
  };

  const handleMenuCloseP = () => {
    setAnchorElP(null);
  };

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/userapp/users/${token['mytoken'].id}/`, {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setAvatar(resp.avatar))
    .catch(error => console.log(error))

  }, [])

  const menuId = 'friend';
  const ITEM_HEIGHT = 38;
  const renderMenu = (
    <Menu
      anchorEl={anchorEl}
      getContentAnchorEl={null}
      id={menuId}
      keepMounted={false}
      anchorOrigin={{
        vertical: 'bottom',
        horizontal: 'right',
      }}
      transformOrigin={{
        vertical: 'top',
        horizontal: 'right',
      }}
      open={isMenuOpen}
      onClose={handleMenuClose}
      PaperProps={{
          style: {
            maxHeight: ITEM_HEIGHT * 4.5,
            width: '20ch',
          },
        }}
    >
      <MenuItem onClick={handleMenuClose}>
        <Avatar className={classes.avatar}/>
        <div className={classes.item}>
          <Typography className={classes.title}>Name</Typography>
          <Typography className={classes.second}>Message</Typography>
        </div>
      </MenuItem>
      <Divider />

      <MenuItem onClick={handleMenuClose}>
        <Avatar className={classes.avatar}/>
        <div className={classes.item}>
          <Typography className={classes.title}>Name</Typography>
          <Typography className={classes.second}>Message</Typography>
        </div>
      </MenuItem>
      <Divider />
      <MenuItem onClick={handleMenuClose}>
        <Avatar className={classes.avatar}/>
        <div className={classes.item}>
          <Typography className={classes.title}>Name</Typography>
          <Typography className={classes.second}>Message</Typography>
        </div>
      </MenuItem>
      <Divider />
      <MenuItem onClick={handleMenuClose}>
        <Avatar className={classes.avatar}/>
        <div className={classes.item}>
          <Typography className={classes.title}>Name</Typography>
          <Typography className={classes.second}>Message</Typography>
        </div>
      </MenuItem>
      <Divider />
      <MenuItem onClick={handleMenuClose}>
        <Avatar className={classes.avatar}/>
        <div className={classes.item}>
          <Typography className={classes.title}>Name</Typography>
          <Typography className={classes.second}>Message</Typography>
        </div>
      </MenuItem>
      <Divider />
    </Menu>
  );

  const renderMenuProfile = (
    <Menu
      anchorEl={anchorElP}
      getContentAnchorEl={null}
      id={menuId}
      keepMounted={false}
      anchorOrigin={{
        vertical: 'bottom',
        horizontal: 'right',
      }}
      transformOrigin={{
        vertical: 'top',
        horizontal: 'right',
      }}
      open={isMenuOpenP}
      onClose={handleMenuCloseP}
      PaperProps={{
          style: {
            maxHeight: ITEM_HEIGHT * 4.5,
            width: '20ch',
          },
        }}
    >
      <MenuItem onClick={handleMenuCloseP}>
        Logout
      </MenuItem>
    </Menu>
  );

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="fixed">
          <Toolbar className={classes.toolbar}>
            <Typography variant="h6" className={classes.LogoLg}>
              Wanwork
            </Typography>
            <Typography variant="h6" className={classes.LogoSm}>
              Ww
            </Typography>
            <div className={classes.search}>
              <Search />
              <InputBase placeholder="Search..." className={classes.input}/>
              <Cancel className={classes.cancel} onClick={() => setOpen(false)} />
            </div>
            <div className={classes.icons}>
              <Search className={classes.searchbtn} onClick={() => setOpen(true)}/>
              <IconButton
                size="large"
                color="inherit"
                onClick={handleProfileMenuOpen}
              >
                <Badge badgeContent={4} color="secondary">
                  <Person />
                </Badge>
              </IconButton>
              <IconButton
                size="large"
                color="inherit"
              >
                <Badge badgeContent={4} color="secondary">
                  <Mail />
                </Badge>
              </IconButton>
              <IconButton
                size="large"
                color="inherit"
              >
                <Badge badgeContent={2} color="secondary">
                  <Notifications />
                </Badge>
              </IconButton>
              <div onClick={() => setOpen(true)}>
                <Avatar src={avatar}/>
              </div>
            </div>
          </Toolbar>
      </AppBar>
      {renderMenu}
      {renderMenuProfile}
    </Box>
  );
}
