import React from 'react'
import { useQuery } from "react-query";
import { connect } from "react-redux";
import { Route,Switch,withRouter,Link } from 'react-router-dom';
import { Home,People,List,Image,PlayCircleOutline,Settings,LocalMall,Forum,
        WbSunny,Menu,Search,Brightness4,Notifications,Mail,Person,ExitToApp,ArrowRight,
        ArrowLeft,Lock,Brightness2 } from '@material-ui/icons';
import { publicRequest,userRequest } from '../../requestMethods';
import './style.css'

function Query(props) {
  return (props.children(useQuery(props.keyName, props.fn, props.options)));
}
export class index extends React.Component {
	constructor(props){
		super(props);

		this.state={
			theme: '',
			profile:[],
			friends: [],
	    profiles: [],
	    user: [],
	    friend:{},
	    conversation: false,
	    messages: [],
	    private_message:[],
	    open: false,
	    checked: false,
		};

		this.handler = this.handler.bind(this);
	}
	getCurrent = async() =>{
		let data;
		await publicRequest.get(`userapp/users/${this.props.user.id}`).then((res) => (data = res.data))
		return data;
	}

	  getCurrentUser = async() =>{
	    let data = await publicRequest.get(`userapp/users/${this.props.user.id}/`)
	    .then(({data}) => data)
	    this.setState({profile: data})
	    if(this.state.profile.theme[0] == ''){
			  	if (this.defaultDark){
			  		this.setState({theme: 'dark', checked: true})
			  	}
			  }else{
			  	if(this.state.profile.theme[0] == "dark"){
				  	this.setState({theme: this.state.profile.theme[0], checked: true})	
				  }else {
				  	this.setState({theme: this.state.profile.theme[0], checked: false})
				  }
			  }
	  };

	  getProfile = async() =>{
			let data = await publicRequest.get(`userapp/users/${this.props.match.params.id}`)
			.then(({data}) => data)
			this.setState({profile: data})
		}

	  getUser = async() =>{
	    let data = await publicRequest.get('userapp/users/')
	    .then(({data}) => data)
	    this.setState({profiles: data})
	  };

	  getFriend = async() =>{
	    let data = await publicRequest.get('userapp/friend/')
	    .then(({data}) => data)
	    this.setState({friends: data})
	  };

	  logout = () => {
	    localStorage.clear()
	    window.location.reload(false);
	  };

	  openBox = () =>{
	  	this.setState({open: !this.state.open})
	  }

	  componentDidMount(){

		  	this.getCurrentUser()
		  	this.getUser()
	    	this.getFriend()
	 };
	render() {
		return (
			<div className="cont-principal">
				<div className="topbars">
					<div className="toggles" onClick={this.styleElement}>
						<Menu className="themes"/>
					</div>
					<div className="searchs">
						<label>
							<input type="text" placeholder="Search ..."/>
							<span><Search /></span>
						</label>
					</div>
					<div className="main-right">
						<div className="topbarStyle">
							<Query
								keyName="users"
								fn={() => this.getCurrent()}
							>
								{({ data, isLoading, error }) => {
	          					if (error) return <h1>Error</h1>;
			          				const events = data ?? []
			          				return(
			          					<div className="badge">
														<Person/>
														{events.friendRequests ? <span>{events.friendRequests}</span> : null}
													</div>
			          				)
			          			}}
							</Query>
						</div>
						<div className="topbarStyle">
							<Notifications/>
						</div>

						<div className="topbarStyle">
							<Mail/>
						</div>
						<div className="topbarStyle" onClick={this.openBox}>
							<img src={this.state.profile.avatar} alt="avatar" />
						</div>
					</div>
				</div>
			</div>
		)
	}
}

const mapStateToProps = (state) => ({
  user: state.user.currentUser,
});
export default connect(mapStateToProps,null)(withRouter(index))
